insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,3,'LinkList','De volgende sjabloon variabelen zijn beschikbaar om uw link lijst sjablonen \r\naan te passen.<br>\r\n \r\n<p>   <b>addlink.url</b><br>\r\nDe URL om een link aan deze link lijst toe te voegen. </p>\r\n<p>  <b>addlink.label</b><br>\r\nHet vertaalde label voor de \'voeg link toe\' URL. </p>\r\n<p>  <b>link_loop</b><br>\r\nDeze lus bevat alle informatie over iedere link. </p>\r\n  <b>link.url</b><br>\r\nDe URL voor deze link. \r\n<p>  <b>link.name</b><br>\r\nDe tekst om naar te linken. </p>\r\n<p>  <b>link.controls</b><br>\r\nDe WebGUI beheersgereedschappen voor deze link. </p>\r\n<p>  <b>link.newwindow</b><br>\r\nEen conditie die aangeeft of deze link in een nieuw venster geopend moet\r\nworden. </p>\r\n<p>  <b>link.description</b><br>\r\nDe beschrijving van deze link. </p>\r\n', 1039543022);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (74,3,'Item','De volgende sjabloon variabelen zijn beschikbaar om uw item sjablonen aan\r\nte passen.<br>\r\n \r\n<p>  <b>attachment.name</b><br>\r\nDe bestandsnaam voor de bijlage bij dit item. </p>\r\n<p>  <b>attachment.url</b><br>\r\nDe URL om de bijlage bij dit item te downloaden. </p>\r\n<p>  <b>attachment.icon</b><br>\r\nDe URL om naar het pictogram bij de bijlage bij dit item. </p>\r\n', 1039542869);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,3,'FileManager','De volgende sjabloon variabelen zijn beschikbaar om Bestandsbeheer sjablonen\r\naan te passen.<br>\r\n<p><b>titleColumn.url</b><br>\r\nDe URL om op titel te sorteren.<br>\r\n</p>\r\n<p><b>titleColumn.label</b><br>\r\nHet vertaalde label voor de titel. </p>\r\n<p>  <b>descriptionColumn.label</b><br>\r\nHet vertaalde label voor de beschrijving. </p>\r\n<p>  <b>descriptionColumn.url</b><br>\r\nDe URL om op beschrijving te sorteren. </p>\r\n<p>  <b>dateColumn.label</b><br>\r\nHet vertaalde label voor de upload datum. </p>\r\n<p>  <b>dateColumn.url</b><br>\r\nDe URL om op upload datum te sorteren. </p>\r\n<p>  <b>search.form</b><br>\r\nWeGUI krachtzoek formulier. </p>\r\n<p>  <b>search.url</b><br>\r\nDe URL om zoek modus aan en uit te zetten. </p>\r\n<p>  <b>search.label</b><br>\r\nHet vertaalde label voor de zoek link. </p>\r\n<p>  <b>addfile.url</b><br>\r\nDe URL om een bestand aan bestandsbeheer toe te voegen. </p>\r\n<p>  <b>addfile.label</b><br>\r\nHet vertaalde label voor de voeg bestand toe link. </p>\r\n<p>  <b>file_loop</b><br>\r\nEen lus die informatie bevat over iedere file die geupload is in deze bestandsbeheer. \r\n</p>\r\n<blockquote> <b>file.canView</b><br>\r\nEen conditie die aangeeft of de huidige gebruiker rechten heeft om dit bestand\r\nte bekijken. \r\n  <p> <b>file.controls</b><br>\r\nDe WebGUI beheergereedschappen voor dit bestand. </p>\r\n  <p> <b>file.title</b><br>\r\nDe titel voor dit bestand. </p>\r\n  <p> <b>file.version1.name</b><br>\r\nDe bestandsnaam voor de eerste versie van dit bestand. </p>\r\n  <p> <b>file.version1.url</b><br>\r\nDe download URL voor de eerste versie van dit bestand. </p>\r\n  <p> <b>file.version1.icon</b><br>\r\nDe URL voor het pictogram voor het bestandstype voor de eerste versie van\r\ndit bestand. </p>\r\n  <p> <b>file.version1.size</b><br>\r\nDe opslaggrootte voor de eerste versie van dit bestand. </p>\r\n  <p> <b>file.version1.type</b><br>\r\nHet type (of bestandsextentie) van de eerste versie van dit bestand. </p>\r\n  <p> <b>file.version1.thumbnail</b><br>\r\nDe URL naar het pictogram voor de eerste versie van dit bestand. </p>\r\n  <p> <b>file.version1.isImage</b><br>\r\nEen conditie die aangeeft of de eerste versie van dit bestand een plaatje\r\nis of niet. </p>\r\n  <p> <b>file.version2.name</b><br>\r\nDe bestandsnaam voor de tweede versie van dit bestand.  </p>\r\n  <p> <b>file.version2.url</b><br>\r\nDe download URL voor de tweede versie van dit bestand.  </p>\r\n  <p> <b>file.version2.icon</b><br>\r\nDe URL voor het pictogram voor het bestandstype voor de tweede versie van\r\ndit bestand.  </p>\r\n  <p> <b>file.version2.size</b><br>\r\nDe opslaggrootte voor de tweede versie van dit bestand.&nbsp; </p>\r\n  <p> <b>file.version2.type</b><br>\r\nHet type (of bestandsextentie) van de tweede versie van dit bestand.&nbsp; \r\n  </p>\r\n  <p> <b>file.version2.thumbnail</b><br>\r\nDe URL naar het pictogram voor de tweede versie van dit bestand.  </p>\r\n  <p> <b>file.version2.isImage</b><br>\r\nEen conditie die aangeeft of de tweede versie van dit bestand een plaatje\r\nis of niet.  </p>\r\n  <p> <b>file.version3.name</b><br>\r\nDe bestandsnaam voor de derde versie van dit bestand.   </p>\r\n  <p> <b>file.version3.url</b><br>\r\nDe download URL voor de derde versie van dit bestand.   </p>\r\n  <p> <b>file.version3.icon</b><br>\r\nDe URL voor het pictogram voor het bestandstype voor de derde versie van\r\ndit bestand.   </p>\r\n  <p> <b>file.version3.size</b><br>\r\nDe opslaggrootte voor de derde versie van dit bestand.&nbsp;  </p>\r\n  <p> <b>file.version3.type</b><br>\r\nHet type (of bestandsextentie) van de derde versie van dit bestand.&nbsp; \r\n </p>\r\n  <p> <b>file.version3.thumbnail</b><br>\r\nDe URL naar het pictogram voor de derde versie van dit bestand.   </p>\r\n  <p> <b>file.version3.isImage</b><br>\r\nEen conditie die aangeeft of de derde versie van dit bestand een plaatje\r\nis of niet.   </p>\r\n  <p> <b>file.description</b><br>\r\nDe beschrijving van dit bestand. </p>\r\n  <p> <b>file.date</b><br>\r\nDe datum waarop dit bestand geupload is. </p>\r\n  <p> <b>file.time</b><br>\r\nDe tijd waarop dit bestand geupload is. </p>\r\n  <p> </p>\r\n</blockquote>\r\n \r\n<p> <b>noresults.message</b><br>\r\nEen vertaald bericht dat aangeeft dat deze bestandsbeheer geen bestanden\r\nheeft die deze gebruiker mag zien.<br>\r\n</p>\r\n<p> </p>\r\n<p> <b>noresults</b><br>\r\nEen conditie die aangeeft of er bestanden zijn die deze gebruiker mag zien. \r\n</p>\r\n<p>  <b>firstPage</b><br>\r\n Een link naar de eerste pagina van de paginanummering.&nbsp;  </p>\r\n<p>  <b>lastPage</b><br>\r\n Een link naar de laatste pagina van de paginanummering.&nbsp;</p>\r\n<p>  <b>nextPage</b><br>\r\n Een link naar de volgende pagina van de paginanummering. </p>\r\n<p>  <b>previousPage</b><br>\r\n Een link naar de vorige pagina van de paginanummering. </p>\r\n<p>  <b>pageList</b><br>\r\n Een lijst van links naar alle pagina\'s in de paginanummering. </p>\r\n<p>  <b>multiplePages</b><br>\r\n Een conditionele variabele die aangeeft of er meer dan een pagina in de\r\npaginanummering is.  </p>\r\n', 1039542744);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (77,3,'FAQ','De volgende sjabloon variabelen zijn beschikbaar om in FAQ sjablonen te gebruiken.<br>\r\n<p><b>addquestion.url</b><br>\r\nDe URL om een vraag aan de FAQ toe te voegen. </p>\r\n<p>  <b>addquestion.label</b><br>\r\n Het vertaalde label voor de voeg vraag toe link. </p>\r\n<p>  <b>qa_loop</b><br>\r\nDe lus die variabelen voor iedere FAQ vraag bevat. </p>\r\n <b>qa.id</b><br>\r\nHet id om deze vraag te identificeren. \r\n<p> <b>qa.answer</b><br>\r\nHet antwoord voor deze vraag. </p>\r\n<p> <b>qa.question</b><br>\r\nDe vraag zelf. </p>\r\n<p> <b>qa.controls</b><br>\r\nDe WebGUI beheergereedschappen voor deze vraag. </p>\r\n', 1039541829);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,3,'Article','De volgende sjabloon variabelen zijn beschikbaar om in artikel sjablonen te\r\ngebruiken.<br>\r\n<p><b>attachment.box</b><br>\r\nLaat een standaard WebGUI bijlage box zien, inclusief icoontje, bestandsnaam\r\nen een bijlage indicator.</p>\r\n<p><b>attachment.icon</b><br>\r\nDe URL naar het icoon-plaatje voor dit sjabloon type. </p>\r\n<p>  <b>attachment.name</b><br>\r\nDe bestandsnaam voor deze bijlage. </p>\r\n<p>  <b>attachment.url</b><br>\r\nDe URL om deze bijlage te downloaden. </p>\r\n<p>  <b>image.thumbnail</b><br>\r\nDe URL naar het pictogram voor het bijgevoegde plaatje. </p>\r\n<p>  <b>image.url</b><br>\r\nDe URL naar het bijgevoegde plaatje. </p>\r\n<p>  <b>post.label</b><br>\r\nHet vertaalde \'label\' om commentaar aan dit artikel toe te voegen. </p>\r\n<p>   <b>post.URL</b><br>\r\nDe URL om commentaar aan dit artikel toe te voegen. </p>\r\n<p>  <b>replies.count</b><br>\r\nHet aantal commentaar dat bij dit artikel toegevoegd is.</p>\r\n<p>  <b>replies.label</b><br>\r\nDe vertaalde tekst die aangeeft dat je de antwoorden kunt bekijken. </p>\r\n<p>  <b>replies.url</b><br>\r\nDe URL om de antwoorden op dit artikel te bekijken. </p>\r\n', 1039541698);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (97,3,'EventsCalendar','Hieronder volgt de lijst met beschikbare sjabloon variabelen voor gebeurtenis\r\nsjablonen.<br>\r\n<p><b>title</b><br>\r\nDe titel van deze gebeurtenis. </p>\r\n<p>  <b>start.label</b><br>\r\nHet vertaalde label voor de begindatum. </p>\r\n<p>  <b>start.date</b><br>\r\nDe datum waarop deze gebeurtenis begint. </p>\r\n<p>  <b>end.date</b><br>\r\nDe datum waarop deze gebeurtenis eindigt. </p>\r\n<p>  <b>end.label</b><br>\r\nHet vertaalde label voor de einddatum. </p>\r\n<p>  <b>canEdit</b><br>\r\nEen conditie die aangeeft of de huidige gebruiker een gebeurtenis kan aanpassen. \r\n</p>\r\n<p>  <b>edit.url</b><br>\r\nDe URL om deze gebeurtenis aan te passen. </p>\r\n<p>  <b>edit.label</b><br>\r\nHet vertaalde label voor de bewerk URL. </p>\r\n<p>  <b>delete.url</b><br>\r\nDe URL om deze gebeurtenis te verwijderen. </p>\r\n<p>  <b>delete.label</b><br>\r\nHet vertaalde label om deze URL te verwijderen. </p>\r\n<p>  <b>previous.url</b><br>\r\nDe URL om de gebeurtenis voor deze gebeurtenis te bekijken. </p>\r\n<p>  <b>previous.label</b><br>\r\nHet vertaalde label voor de vorige URL. </p>\r\n<p>  <b>next.label</b><br>\r\nHet vertaalde label voor de volgende URL. </p>\r\n<p>  <b>next.url</b><br>\r\nDe URL om de gebeurtenis na deze gebeurtenis te bekijken. </p>\r\n<p>  <b>description</b><br>\r\nDe beschrijving van deze gebeurtenis. </p>\r\n', 1039541021);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (95,3,'EventsCalendar','De volgende sjabloon variabelen zijn beschikbaar om uw evenementen kalender\r\naan te passen.<br>\r\n<p><b>addevent.url</b><br>\r\nDe URL om een gebeurtenis aan de kalender toe te voegen. </p>\r\n<p>  <b>addevent.label</b><br>\r\nHet vertaalde label voor de link om een gebeurtenis toe te voegen. </p>\r\n<p>  <b>calendar.big</b><br>\r\nEen&nbsp; paginavullende kalender. </p>\r\n<p>  <b>calendar.small</b><br>\r\nEen kleine kalender die alleen datumnummers laat zien. </p>\r\n<p>  <b>calendar.firstPage</b><br>\r\nEen link naar de eerste pagina van de kalender paginanummering. </p>\r\n<p>  <b>calendar.lastPage</b><br>\r\nEen link naar de laatste pagina van de kalender paginanummering.&nbsp; </p>\r\n<p>  <b>calendar.nextPage</b><br>\r\nEen link naar de volgende pagina van de kalender paginanummering. <br>\r\n</p>\r\n<p><b>calendar.previousPage</b><br>\r\nEen link naar de vorige pagina van de kalender paginanummering.&nbsp; </p>\r\n<p>  <b>calendar.pageList</b><br>\r\nEen lijst van links naar alle pagina\'s in de kalender paginanummering. </p>\r\n<p>  <b>calendar.multiplePages</b><br>\r\nEen conditionele variabele die aangeeft of er meer dan een pagina in de kalender\r\npaginanummering is. </p>\r\n<p>  <b>list_loop</b><br>\r\nEen lus die de \'lijst layout\' lijst van een kalender bevat.<br>\r\n</p>\r\n<p></p>\r\n<blockquote><b>list.date</b><br>\r\nDe datum van deze gebeurtenis. \r\n  <p> <b>list.title</b><br>\r\nDe titel van deze gebeurtenis. </p>\r\n  <p> <b>list.description</b><br>\r\nDe beschrijving van deze gebeurtenis. </p>\r\n  <p> <b>list.sameAsPrevious</b><br>\r\nEen conditionele variabele die aangeeft of deze gebeurtenis dezelfde datum\r\nheeft als de laatse gebeurtenis. </p>\r\n  <p> <b>list.url</b><br>\r\nDe URL naar de paginavullende layout voor deze gebeurtenis. </p>\r\n  <p> <b>list.controls</b><br>\r\nDe WebGUI beheergereedschappen voor deze gebeurtenis.&nbsp; </p>\r\n  <p>  </p>\r\n</blockquote>\r\n \r\n<p>  <b>list.firstPage</b><br>\r\nEen link naar de eerste pagina van de paginanummering.&nbsp; </p>\r\n<p>  <b>list.lastPage</b><br>\r\nEen link naar de laatste pagina van de paginanummering.&nbsp;&nbsp; </p>\r\n<p>  <b>list.nextPage</b><br>\r\nEen link naar de volgende pagina van de paginanummering.&nbsp; <br>\r\n </p>\r\n<p>  <b>list.previousPage</b><br>\r\nEen link naar de vorige pagina van de paginanummering.&nbsp; <br>\r\n </p>\r\n<p>  <b>list.pageList</b><br>\r\nEen lijst van links naar alle pagina\'s in de paginanummering.<br>\r\n</p>\r\n<p><b>list.multiplePages</b><br>\r\nEen conditionele variabele die aangeeft of er meer dan een pagina in de paginanummering\r\nis. </p>\r\n', 1039540752);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (826,3,'WebGUI','WebGUI heeft een krachtige sjabloontaal ingebouwd om u maximale controle\r\nte geven over de layout van de inhoud. Hieronder volgt een lijst van de sjabloon\r\ntaal syntax.<br>\r\n<p><b>NB I: </b>Let erop dat de woorden <i>foo</i> en <i>bar </i>bedoeld\r\nzijn als plaatshouders voor eigenlijke variabele namen die u zult gebruiken.\r\nZe horen niet bij de sjabloontaal. <br>\r\n<b>NB II: </b>Aangezien deze taal in het engels is (en niet vertaald kan\r\nworden) zullen alle variabele namen en andere zaken ook engelstalig zijn\r\n(zoals het woord template voor sjabloon).<br>\r\n<b><br>\r\nVariabelen</b><br>\r\n Variabelen zijn de meest simpele sjabloon commando\'s. Ze worden gebruikt\r\nom stukken inhoud te positioneren.  </p>\r\n<p> <i>Syntax:</i> &lt;tmpl_var foo&gt; or &lt;tmpl_var name=\"foo\"&gt; </p>\r\n<p>  <i>Voorbeeld:</i> &lt;tmpl_var name&gt; </p>\r\n<p>  <b>Condities</b><br>\r\nVoor programmeurs zijn condities niets nieuws, maar voor designers kunnen\r\nze een beetje verwarrend zijn. Condities zijn eigenlijk gewoon waar of niet\r\nwaar vragen en als je er zo over denkt hoeven ze geen enkel probleem op te\r\nleveren. (If = als, Else = anders, Unless = tenzij)<br>\r\n<i><br>\r\nSyntax:</i> &lt;tmpl_if foo&gt; &lt;tmpl_else&gt; &lt;/tmpl_if&gt; <br>\r\n <i>Syntax:</i> &lt;tmpl_unless foo&gt; &lt;tmpl_else&gt; &lt;/tmpl_unless&gt; \r\n</p>\r\n<p>  <i>Voorbeeld:</i> &lt;tmpl_if isTrue&gt; Het was waar!&lt;tmpl_else&gt;\r\nHet was niet waar! &lt;/tmpl_if&gt; </p>\r\n<p>  <b>Lussen</b><br>\r\nLussen itereren over een lijst van gegevens. Lussen zijn iets moeilijker\r\nte gebruiken dan variabelen, maar ze zijn stukken krachtiger.<br>\r\n<i><br>\r\nSyntax:</i> &lt;tmpl_loop foo&gt; &lt;/tmpl_loop&gt; </p>\r\n<p>  <i>Voorbeeld:</i> <br>\r\n &lt;tmpl_loop users&gt; <br>\r\n   &nbsp; Naam: &lt;tmpl_var first_name&gt;&lt;br/&gt;<br>\r\n &lt;/tmpl_loop&gt; </p>\r\n<p>  <b>Lus condities</b><br>\r\nLussen hebben speciale conditie-variabelen. Deze zijn __FIRST__ (eerste),\r\n__ODD__ (oneven), __INNER__ (binnenste) en __LAST__ (laatste). </p>\r\n<p>  <i>Examples:</i><br>\r\n </p>\r\n<pre>   &lt;TMPL_LOOP FOO&gt;<br>      &lt;TMPL_IF __FIRST__&gt;<br>        Dit wordt alleen bij de eerste lus getoond.<br>      &lt;/TMPL_IF&gt;<br><br>      &lt;TMPL_IF __ODD__&gt;<br>        Dit wordt om de andere lus getoond in de oneven lussen.<br>      &lt;/TMPL_IF&gt;<br><br>      &lt;TMPL_UNLESS __ODD__&gt;<br>        Dit wordt om de andere lus getoond in de even lussen.<br>      &lt;/TMPL_UNLESS&gt;<br><br>      &lt;TMPL_IF __INNER__&gt;<br>        Dit wordt getoond bij lussen die niet het eerste, noch het laatste zijn.<br>      &lt;/TMPL_IF&gt;<br><br>      &lt;TMPL_IF __LAST__&gt;<br>        Dit wordt alleen bij de laatste lus getoond.<br>      &lt;TMPL_IF&gt;<br>   &lt;/TMPL_LOOP&gt;<br></pre>\r\n  \r\n<p> <b>NB:</b> Zowel de sjabloontaal als de sabloon variabelen zijn niet\r\nafhankelijk van hoofd of kleine letters (case insensitive).<br>\r\n</p>\r\n', 1039540120);
