insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'FAQ','Het lijkt alsof elke website, intranet en extranet in de wereld een Frequently\r\nAsked Questions (FAQ, in het Nederlands: Vaak Gestelde Vragen) sectie heeft.\r\nDit wobject biedt hulp bij het bouwen van zo\'n sectie.<br>\r\n<br>\r\n<b>Inhoudsopgave Aanzetten?</b><br>\r\nMoet er een inhoudsopgave weergegeven worden bij deze FAQ? Een inhoudsopgave\r\nis een lijst met vragen bovenaan de FAQ, waarbij de vragen een link hebben\r\ndie naar het onderstaande antwoord verwijst.<br>\r\n<br>\r\n<b>V/A Aanzetten?</b><br>\r\nSommige mensen willen een <b>V:</b> voor elke vraag en een <b>A:</b> voor\r\nelk antwoord weergeven. Deze knop zet dit aan.<br>\r\n<br>\r\n<b>[top] Link Aanzetten?</b><br>\r\nIs het gewenst dat er achter elk antwoord een link naar de bovenkant van\r\nde pagina staat.<br>\r\n<br>\r\n<b>Verder Gaan Naar Vraag Toevoegen?</b><br>\r\nLaat dit aangevinkt staan als direct na het maken van de FAQ sectie vragen\r\ntoegevoegd gaan worden.<br>\r\n<br>\r\n<hr width=\"100%\" size=\"1\" noshade=\"noshade\"><i><b>N.B.:</b></i> De volgende\r\nstijl is specifiek voor het FAQ wobject.<br>\r\n<br>\r\n<b>.faqQuestion</b><br>\r\nEen FAQ vraag, dit ter onderscheiding van een antwoord.<br>\r\n', 1038526046);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (636,3,'WebGUI','Volg deze simpele stappen om een pakket te maken: \r\n<ol>\r\n  <li>Selecteer \"Beheer Pakketten\" uit het administratieve functies menu</li>\r\n  <li>Voeg een pagina toe en geef de pagina een naam. Deze naam is meteen\r\nook de naam van het pakket.</li>\r\n  <li>Ga naar de nieuwe pagina en voeg paginas en wobjecten toe. Elke pagina\r\nof wobject die toegevoegd worden zullen iedere keer dat dit pakket ontplooid\r\nwordt, gemaakt worden.</li>\r\n</ol>\r\n<b>Aantekeningen:</b><br>\r\nEen gebruiker kan alleen pakketten toevoegen, bewerken of verwijderen, als\r\nhij/zij tot de Pakketten Beheerders of Beheerders groep behoort.<br>\r\n<br>\r\nDe inhoud die zich in een wobject bevindt wordt automatisch gekopieerd wanneer\r\nhet pakket ontplooid wordt.<br>\r\n<br>\r\nPrivileges en stijlen die tot paginas behoren worden niet gekopieerd wanneer\r\nhet pakket ontplooid wordt. De paginas nemen de privileges en stijlen aan\r\ndie in het gebied staan waar het pakket ontplooid wordt.<br>\r\n', 1038525178);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'FileManager','De Dwonload Beheerder is ontworpen om te helpen de bestanden te distriburen\r\nop de site. Hiermee kan gespecificeerd worden wie bestanden mag downloaden\r\nvan de site.<br>\r\n<br>\r\n<b>Breek Af Na</b><br>\r\nHoeveel bestanden moeten er weergegeven worden voordat de bestanden over\r\nmeer paginas verdeeld worden? Met andere woorden: hoeveel bestanden moeten\r\ner getoond worden per pagina?<br>\r\n<br>\r\n<b>Miniaturen Weergeven?</b><br>\r\nVink dit aan als het gewenst is miniaturen te tonen van afbeeldingen die\r\nzijn ingeladen. Let op dat alleen het miniatuur van de hoofdbijlage wordt\r\nweergegeven, die van andere versies niet.<br>\r\n<br>\r\n<b>Verder Gaan Naar Toevoegen Download?</b><br>\r\nLaat dit aangevinkt staan wanneer meteen down te loaden bestanden toegevoegd\r\nworden.<br>\r\n', 1038524323);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (6,3,'WobjectProxy','Met de Wobject Proxy kan een wobject van een andere pagina gespiegeld worden\r\nnaar een willekeurige pagina. Dit is praktisch als inhoud in verschillende\r\ndelen van de site gebruikt wordt.<br>\r\n<br>\r\n<b>Wobject Naar Proxy</b><br>\r\nSelecteer het wobject dat naar de proxy moet. Het selectie veld neemt de\r\nvolgende vorm aan: <b>\"Pagina Titel / Wobject Naam\" (Wobject Id)</b> zodat\r\nsnel en precies het wobject dat je zoekt kan vinden.<br>\r\n', 1038523409);
