insert into international (internationalId,languageId,namespace,message,lastUpdated) values (637,3,'WebGUI','<b>Voornaam</b><br>\r\nDe voornaam van deze gebruiker.<br>\r\n<br>\r\n<b>Andere Voornamen</b><br>\r\nDe andere voornamen van deze gebruiker.<br>\r\n<br>\r\n<b>Achternaam</b><br>\r\nDe achternaam van deze gebruiker<b><br>\r\n<br>\r\nEmail adres</b><br>\r\nHet email adres van deze gebruiker. Dit moet alleen gespecificeerd worden\r\nals de gebruiker deel gaat nemen in functies waarbij email nodig is.<br>\r\n<br>\r\n<b>ICQ UIN</b><br>\r\nHet <a href=\"http://www.icq.com\">ICQ</a> UIN is het \"User ID Number\" (gebruikers-identificatie-nummer)\r\nop het ICQ netwerk. ICQ is een populair platform waar de berichten meteen\r\nworden weergegeven.<br>\r\n<br>\r\n<b>AIM Id</b><br>\r\nDe account identificatie voor het <a href=\"http://www.aim.com\">AOL Instant\r\nMessenger</a> systeem.<br>\r\n<br>\r\n<b>MSN Messenger Id</b><br>\r\nDe account identificatie voor het <a href=\"http://messenger.msn.com\">Microsoft\r\nNetwork Instant Messenger</a> systeem.<br>\r\n<br>\r\n<b>Yahoo! Messenger Id</b><br>\r\nDe account identificatie voor het <a href=\"http://messenger.yahoo.com\">Yahoo!\r\nInstant Messenger</a> systeem.<br>\r\n<br>\r\n<b>Mobiel Nummer</b><br>\r\nHet mobiele telefoonnummer van deze gebruiker.<br>\r\n<br>\r\n<b>Pieper</b><br>\r\nHet telefoonnummer van de pieper van deze gebruiker.<br>\r\n<br>\r\n<b>Email naar Pieper Gateway</b><br>\r\nHet email adres vande gebruiker waarmee tekst berichten naar de pieper gestuurd\r\nkunnen worden.<br>\r\n<br>\r\n<b>Adresinformatie</b><br>\r\nHet post- of huisadres van deze gebruiker.<br>\r\n<br>\r\n<b>Werkadresinformatie</b><br>\r\nHet post-of huisadres van het bedrijf van deze gebruiker.<br>\r\n<br>\r\n<b>Geslacht</b><br>\r\nHet geslacht van deze gebruiker.<br>\r\n<br>\r\n<b>Geboortedatum</b><br>\r\nDe geboortedatum van deze gebruiker.<br>\r\n<br>\r\n<b>Taal</b><br>\r\nWelke taal moet gebruikt worden voor systeem gerelateerde berichten.<br>\r\n<br>\r\n<b>Tijds Correctie</b><br>\r\nEen aantal uren (positief of negatief) die het verschil tussen de tijd van\r\nde server en de gebruiker. Deze functie wordt gebruikt om het verschil in\r\ntijdszones te corrigeren.<br>\r\n<br>\r\n<b>Eerste dag van de week</b><br>\r\nDe eerste dag van de week op de kalender van deze gebruiker. In de USA is\r\ndit zondag maar in andere landen is het ook wel eens maandag.<br>\r\n<br>\r\n<b>Datum Opmaak</b><br>\r\nWelke opmaak moet de datum op deze pagina gebruiken.<br>\r\n<br>\r\n<b>Tijd Opmaak</b><br>\r\nWelke opmaak moet de tijdsaanduiding op deze pagina gebruiken.<br>\r\n<br>\r\n<b>Indeling Discussie</b><br>\r\nMoet de discussie weergegeven worden in &eacute;&eacute;n lijn of in een\r\nboomconstructie? De eerste optie zet alle antwoorden op &eacute;&eacute;n\r\npagina in chronologische volgorde. De tweede optie toont een hierarchische\r\nlijst van antwoorden.<br>\r\n<br>\r\n<b>Inbox Melding</b><br>\r\nHoe moet aan deze gebruiker gemeld worden wanneer er een nieuw WebGUI bericht\r\nis ontvangen.<br>\r\n', 1038521357);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (634,3,'WebGUI','<b>Standaard Thuis Pagina (Home Page)</b><br>\r\nSommige kleine paginas hebben geen thuispagina, maar gebruiken een interne\r\npagina zoals \"Bedrijfs Informatie\" als hun thuispagina. Om deze reden kan\r\nelke pagina op de site de standaard pagina zijn. Mensen krijgen dan deze\r\npagina te zien als ze alleen het URL opgeven of wanneer ze op de Thuis link\r\nklikken die gemaakt is met het ^H; macro.<br>\r\n<br>\r\n<b>Niet Gevonden Pagina</b><br>\r\nAls een pagina die opgevraagd wordt die zich niet in het systeem bevindt,\r\nkan de gebruiker naar de thuispagina of een foutmeldingspagina verwezen worden.\r\nDan kan de gebruiker verder zoeken. Hier kan besloten worden wat het beste\r\nis voor de gebruikers.<br>\r\n<br>\r\n<b>Document Type verklaring</b><br>\r\ntegenwoordig kan een site bekeken worden met verschillende browsers, inclusief\r\ngeautomatiseerde browsers zoals zoekmachines. De meeste browsers willen weten\r\nwat voor type inhoud er op de pagina staat. Met het doctype etiket&nbsp;\r\nkan dit gespecificeerd worden. WebGUI genereert standaard een inhoud die\r\nmet HTML 4.0 bekeken kan worden.<br>\r\n<br>\r\n<b>Voeg bewerkings stempel toe aan bijdragen?</b><br>\r\nWanneer een gebruiker een bijdrage bewerkt op een berichten bord, wordt een\r\nstempel toegevoegd zodat de gebruiker geidentificeerd kan worden en bepaald\r\nkan worden wanneer de bijdrage is gemaakt. Op sommige paginas is deze informatie\r\noverbodig, daarom kan deze functie hier ook uitgezet worden.<br>\r\n<br>\r\n<b>Bijgedragen HTML filteren</b><br>\r\nVooral wanneer een site publiekelijk toegankelijk is en iedereen bijdragen\r\nkan leveren aan bijvoorbeeld berichtenborden is het een goed idee om de inhoud\r\nte filteren voor beschadigende code, die het bekijken door bezoekers kan\r\nbemoeilijken.. In sommige omstandigheden kan het zelfs leiden tot veiligheidsproblemen.\r\nGebruik deze instelling om te bepalen hoeveel er gefilterd moet worden.<br>\r\n<br>\r\n<b>Maximum Grootte Bijlagen</b><br>\r\nDe maximum grootte (in kilobytes) van bijlagen die ingeladen kunnen worden\r\nin het systeem.<br>\r\n<br>\r\n<b>Maximale Afmeting Afbeeldingen</b><br>\r\nWanneer afbeeldingen, die groter zijn dan de maximale afmeting, in het systeem\r\nworden ingeladen, worden de afmetingen bepaald op de maximale afmetingen.\r\nDe maximale afmeting worden gemeten in pixels en zal de langste zijde van\r\nde afbeelding gebruiken om te kijken of de limiet is overschreden.<br>\r\n<br>\r\n<b>Miniatuur Grootte</b><br>\r\nWanneer afbeeldingen in het systeem worden ingeladen, zullen ze automatisch\r\nminiaturen hebben die gegenereerd zijn tot de grootte die hier wordt ingevoerd.\r\nMiniatuur grootte wordt in pixels gemeten.<br>\r\n<br>\r\n<b>Rijen Tekst Gebieden</b><br>\r\nSommige paginas willen de grootte van de formulieren die WebGUI maakt kunnen\r\ncontroleren. Met deze instelling kan het aantal rijen met karakters, dat\r\nweergegeven wordt in de tekst gebieden, gespecificeerd worden.<br>\r\n<br>\r\n<b>Kolommen Tekst Gebieden</b><br>\r\nSommige paginas willen de grootte van de formulieren die WebGUI maakt kunnen\r\ncontroleren. Met deze instelling kan het aantal kolommen met karakters, dat\r\nweergegeven wordt in de tekst gebieden, gespecificeerd worden.<br>\r\n<br>\r\n<b>Grootte Tekst Velden</b><br>\r\nSommige paginas willen de grootte van de formulieren die WebGUI maakt kunnen\r\ncontroleren. Met deze instelling kan het aantal karakters dat in &eacute;&eacute;n\r\nkeer in tekst velden weergegeven wordt gespecificeerd worden.<br>\r\n<br>\r\n<b>De te gebruiken Editor</b><br>\r\nWebGui heeft de uitgebreide Rich Editor die gebruikers in staat stelt tekst\r\nte bewerken alsof ze met Microsoft Word&reg; of een ander tekstverwerkingsprogramma\r\nwerken. Om dit te gebruiken moet \"Built-In Editor\" geselcteerd worden. Soms\r\nzijn nog meer ontwikkelde editors nodig voor bijvoorbeeld Spell Check. Daarom\r\nkan een derde editor, <a href=\"http://www.realobjects.de\">Real Objects Edit-On\r\nPro&reg;</a> geinstalleerd worden. Nadat het geinstalleerd is verander dan\r\ndeze optie. Wanneer er meer gedetailleerde instructie nodig is, kan deze\r\ngevonden worden bij <a href=\"http://www.plainblack.com/ruling_webgui\">Ruling\r\nWebGUI.</a><br>\r\n \r\n', 1038519142);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (635,3,'WebGUI','Pakketten zijn groepen van pagina en wobjecten die voorbereid zijn, zadat ze samen ingezet kunnen worden. Een pakketten beheerder kan het nodig achten een pakket te maken dat op verscheidene paginas een berichtenbord, een FAQ en een peiling heeft, omdat ze vaak gebruikt worden. Pakketten worden meestal samengesteld om de herhalende taken te verlichten.<br><br>\r\nEen pakket dat vaak gemaakt wordt is een Pagina/Artikel pakket. Het is vaak het geval dat iemand een pagina wilt toevoegen met een Artikel voor de inhoud. In plaats van de stappen van het maken van een pagina (pagina toevoegen, naar de pagina gaan en dan het artikel toevoegen) te nemen, kan ook een pakket gemaakt worden die al deze stappen in 1 keer maakt.', 1038513818);
