insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,3,'LinkList','<b>Titel</b><br>\r\nText die gelinked zal worden.\r\n<p>\r\n<b>URL</b><br>\r\nDe Website waarnaar gelinked wordt.\r\n<p>\r\n\r\n<b>In een nieuw venster openen?</b><br>\r\nSelecteer ja als u wilt dat deze link in een nieuw venster geopend wordt.\r\n<p>\r\n\r\n<b>Omschrijving</b><br>\r\nBeschrijf de site waar u naar linkt. Dit kan weggelaten worden als u dat wilt.\r\n<p>\r\n\r\n<b>Nog een link toevoegen?</b><br>\r\nAls u nog een link toe wilt voegen, selecteer dan ja.\r\n<p>\r\n', 1038513307);
