insert into international (internationalId,languageId,namespace,message,lastUpdated) values (638,3,'WebGUI','Sjablonen bepalen hoe paginas in WebGUI worden ingedeeld. De meeste paginas hebben tegenwoordig meer menus en een groot tekst gebied. Veel hebben drie of vier kolommen vooraf gegaan door verscheidene headers en/of banners gebieden. Complexe indelingen worden in WebGUI mogelijk gemaakt door het gebruik van Sjablonen. Er zijn verscheidene sjablonen meegeleverd met WebGUI, maar er kunnen ook eigen sjblonen gemaakt worden.', 1038513028);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (639,3,'WebGUI','<b>Sjabloon Naam</b><br>\r\nGeef dit sjabloon een beschrijvende naam, zodat bekend is welk sjabloon aan een pagina wordt toegevoegd.<br><br>\r\n<b>Sjabloon</b><br>\r\nMaak het sjabloon door de speciale macros ^0;, ^1;, ^2; etc in het sjabloon te plaatsen om de verschillende inhouds gebieden te representeren. Dit wordt normaal gesproken gedaan door een tabel te gebruiken om de inhoud te plaatsen. Kijk ook naar de met WebGUI meegeleverde sjablonen voor ideeen.', 1038512211);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (640,3,'WebGUI','Sjablonen verwijderen zonder te weten wat voor bijwerkingen dit kan hebben is niet verstandig (sommige paginas kunnen het sjabloon nog gebruiken). Wanneer ervoor gekozen wordt een sjabloon te verwijderen, zulleen alle paginas die dit sjabloon gebruiken het standaard sjabloon gaan gebruiken.', 1038511714);
