insert into international (internationalId,languageId,namespace,message,lastUpdated) values (698,3,'WebGUI','De methode waarbij de activiteiten van gebruikers gevolgd worden heet karma.\r\nKarma kan ook gebruikers ook belonen of straffen voor hun activiteitenniveau.\r\nZodra karma wordt gebruikt zullen menus van vele dingen in WebGUI veranderen\r\nom karma weer te geven.<br>\r\n<br>\r\nEr kan gevolgd worden wanneergebruikers inloggen en hoeveel ze bijdragen\r\naan de pagina. Ook kunnen gebruikers extra mogelijkheden krijgen door hun\r\nkarma niveau.<br>\r\n<br>\r\nMeer informatie kan gevonden worden op <a\r\n href=\"http://www.plainblack.cum/ruling_webgui\">Ruling WebGUI</a>.<br>\r\n', 1038506195);
