insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,3,'MailForm','Dit wobject maakt een eenvoudig formulier dat <br>\r\n<br>\r\n<b>Breedte</b><br>\r\nDe breedte van alle velden in het formulier. De standaard waarde is 45.<br>\r\n<br>\r\n<b>Van, Naar, Cc, Bcc, Onderwerp</b><br>\r\nDeze velden controleren het uiterlijk van de email wanneer hij verstuurd\r\nwordt en naar wie. De bezoekers van de pagina kunnen de mogelijkheid gegeven\r\nworden sommige of alle velden aan te passen. De \"Van\" en \"Onderwerp\" velden\r\nzijn meestal de enige velden waarvan het gewenst is dat de gebruiker ze kan\r\nbewerken. Gebruik de opties bij elk veld om te kiezen of een gebruiker dit\r\nveld wel of niet kan zien of bewerken.<br>\r\n<br>\r\nEr kan ook in het \"Naar\" veld een WebGUI gebruikersnaam of groep ingevoerd\r\nworden. De email wordt dan gestuurd naar de corresponderende gebruiken of\r\ngroep.<br>\r\n<br>\r\n<b>Bevestiging</b><br>\r\nDit bericht wordt weergegevn nadat de gebruiker op \"Verzenden\" heeft geklikt.<br>\r\n<br>\r\n<b>Opslaan Gegevens?</b><br>\r\nWanneer deze op \"Ja\" staat worden de ingevulde gegevens opgeslagen in de\r\ndatabase, zodat ze later bekeken kunnen worden. De applicatie voor het bekijken\r\nvan deze database is nog niet verkrijgbaar, maar zodra dit wel zo is kunnen\r\nde gegevens bekeken worden in een centrale locatie.<br>\r\n<br>\r\n<b>Verder gaan naar toevoegen velden?</b><br>\r\nLaat dit aangevinkt staan, wanneer er direct extra velden aan het formulier\r\ntoegevoegd worden.<br>\r\n', 1038506142);
