insert into international (internationalId,languageId,namespace,message,lastUpdated) values (828,2,'WebGUI','Die meisten Wobjects haben Templates, dies es Ihnen erlauben, das Layout des Wobject-Benutzerinterfaces zu verändern. Diese Wobjects enthalten alle ein Standardset von Template Variabeln, die sie fürs Layout benutzen können, aber auch ihre eigenen spezifischen Variabeln. Nachfolgend eine Liste der allgemeinen Template Variabeln die alle Wobjects betreffen.<br><br>\r\n<b>title</b><br>\r\nDer Titel für das Wobject<br><br>\r\n<b>displayTitle</b><br>\r\nEine bedingte Variable, ob der Titel angezeigt werden soll, oder nicht.<br><br>\r\n<b>description</b><br>\r\nDie Beschreibung des Wobjects.<br><br>\r\n<b>wobjectId</b><br>\r\nDie ID, die WebGUI zum Ansteuern dieses Wobjects benutzt.', 1041623335);
