insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,2,'FileManager','<b>Dateititel</b><BR>Der Text, der für diese Datei angezeigt wird. Falls Sie \r\nhier nichts angeben, wird der Dateiname angezeigt. \r\n<P><b>Dateiname</b><BR>Wählen Sie hier eine Datei von Ihrem lokalen Laufwerk, \r\ndie Sie uploaden möchten. \r\n<P><b>Alternative #1</b><BR>Eine alternative Version dieser Datei. Wenn z. B. \r\ndie Datei im  JPEG-Format ist, könnten Sie bei der alternativen Version z. B. \r\nTIFF oder BMP auswählen. \r\n<P><b>Alternative #2</b><BR>Analog zur Alternative #1. \r\n<P><b>Kurze Beschreibung</b><BR>Eine kurze Beschreibung dieser Datei. Beachten \r\nSie, dass Sie Schlüsselwörter benutzen, damit die Benutzer besser nach dieser \r\nDatei suchen können. \r\n<P><b>Gruppe, die Download benutzen kann</b><BR>Wählen Sie die Gruppe aus, die \r\ndiese Datei downloaden kann. \r\n<P><b>Als nächstes?</b><BR>Möchten Sie sofort&nbsp;einen neuen Downloa!
 d hinzufügen? \r\nOder möchten Sie zuerst zurück zur Seite gehen?. </P>', 1041598859);
