insert into international (internationalId,languageId,namespace,message,lastUpdated) values (620,2,'WebGUI','Wie diese Funktion bereits andeutet werden Sie eine Gruppe löschen und alle \r\ndarin befindlichen Benutzer aus dieser Gruppe zu entfernen. Achten Sie darauf, \r\ndass Benutzer in dieser Gruppe nicht vielleicht die Berechtigung für Seiten \r\nverlieren etc. \r\n<P>Wie mit jedem Löschvorgang werden Sie gefragt, ob sie sich wirklich sicher \r\nsind, diese Gruppe zu löschen. Wenn Sie mit &quot;Ja&quot; antworten, wird \r\nder Löschvorgang durchgeführt, andernfalls gelangen sie zur vorherigen Seite \r\nzurück.</P>', 1044377232);
