insert into international (internationalId,languageId,namespace,message,lastUpdated) values (619,2,'WebGUI','Diese Funktion löscht das gewählte Wobject dauerhaft von einer Seite. Wenn sie \r\nsich nicht sicher sind, ob sie dieses Element löschen möchten, können Sie es \r\nauch ausschneiden und in die Zwischenablage legen und es dann irgendwann löschen. \r\n<P>Wie mit jedem Löschvorgang werden Sie gefragt, ob sie sich wirklich sicher \r\nsind, dieses Wobject zu löschen. Wenn Sie mit &quot;Ja&quot; antworten, wird \r\nder Löschvorgang durchgeführt, andernfalls gelangen sie zur vorherigen Seite \r\nzurück.</P>', 1044376958);
