insert into international (internationalId,languageId,namespace,message,lastUpdated) values (618,2,'WebGUI','<B>Passwort wiederherstellen Text</B><BR>Der Text, der einem Benutzer gesendet \r\nwird, wenn er die &quot;Passwort wiederherstellen&quot; Funktion benutzt.<BR><BR><B>Email Fussbereich</B><BR>Dieser \r\nFusstext ist für Makros vorgesehen und wird jeder Email, die von WebGUI versandt \r\nwird, angehängt.<P><B>SMTP Server</B><BR>Dies ist die Adresse ihres lokalen \r\nMailservers. Diese wird in allen Funktionen benötigt, die das Internet Emailsystem \r\nverwenden (wie z. B. Passwort wiederherstellen). \r\n<P>Optional können Sie auch den Pfad zu sendmail angeben, wenn ein Sendmail-Server \r\nauf der gleichen Maschine wie WebGUI läuft. Bei den meisten Linux-Servern ist \r\ndies &quot;/usr/lib/sendmail&quot;. </P>', 1044376779);
