insert into international (internationalId,languageId,namespace,message,lastUpdated) values (614,2,'WebGUI','Styles werden benutzt, um das &quot;Look and feel&quot; ihrer WebGUI Seiten \r\nzu bestimmen. Mit WebGUI können&nbsp;sie eine unbegrenzte Anzahl von&nbsp;Styles \r\nverwenden, daher können sie so viele verschiedene Darstellungen benutzen, wie \r\nsie möchten. Sie könnten z. B. Seiten haben, die dem Layout bzw. Corporate Design \r\nihres Unternehmens entsprechen, oder Seiten, die dem Erscheinungsbild von Yahoo \r\nähnlich sind, usw. Genauso können sie auch Seiten erstellen, die wie die Seiten \r\neines Buches aussehen. Mit dem Styleverwaltungs-Tool haben Sie die ultimative \r\nKontrolle über all ihre Designs.<P>Es gibt bereits verschiedene in WebGUI eingebaute \r\nStyles. Die ersteren werden von WebGUI selbst benutzt und sollten daher &nbsp;nicht \r\nbearbeitet oder gelöscht werden. Die letztern sind einige Beispiel-Styles und \r\nkönnen als Vorlage für neue Styles ver  wendet oder auch gelöscht werden. \r\n<P><B>Clipboard</B><BR>Dieser Style wird von der Zwischenablage benötigt<P><B>Fail Safe</B><BR>Wenn \r\nsie einen Style löschen, der bereits auf einigen Seiten in Benutzung ist, wird \r\ndieser FailSafe-Style diesen Seiten zugeordnet. Der Style hat einen weissen \r\nHintergrund und eine einfache Navigation. \r\n<P><B>Make Page Printable</B><BR>Dieser Style wird benutzt, wenn sie ein  <B>^r;</B> \r\nMakro in Ihren Seiten einsetzen und der Besucher dann darauf klickt - er erhält \r\ndann die Druckversion der Seite. Sie haben hier die Möglichkeit, ein Logo und \r\neine Copyright-Meldung auf der Seite einzubinden. \r\n<P><B>Packages</B><BR>Dieser Style wird vom Paketmanager benutzt. \r\n<P><B>Trash</B><BR>Dieser Style wird vom Mülleimer genutzt. \r\n<P>\r\n<HR>\r\n<B>Demo Style</B><BR>Dies ist ein Beispiellayout, das von einer Templates-Seite \r\ngenommen wurde\r\n(www.freewebtemplates.com). \r\n<P><B>Plain Black Software (black) &amp; (white)</B><BR>Diese Layouts wurden \r\nauf der Plainblackseite verwendet.  \r\n<P><B>Yahoo!®</B><BR>Dies ist das Design von Yahoo!®. (genutzt ohne Berechtigung.) \r\n<P><B>WebGUI</B><BR>Dies ist ein einfaches Design mit WebGUI logos. \r\n<P><B>WebGUI 4</B><BR>Dieser Style wurde seit WebGUI Version 4 verwendet. </P>', 1044376312);
