insert into international (internationalId,languageId,namespace,message,lastUpdated) values (836,8,'WebGUI','I Frammenti sono parti di testo o altri elementi che possono essere usati\r\nripetutamente sul tuo sito. Pensa a&nbsp; java scripts, style sheets, filmati\r\nflash , o anche slogan o pay-off questi sono esempi di Frammenti. Il Bello è\r\nche se hai bisogno di cambiare il testo etc, puoi cambiarlo una volta sola!\r\n<p><b>Nome</b><br>\r\nDai a questo frammento un nome univoco da utilizzare in seguito per poterlo\r\nrichiamare.\r\n<p><b>Organizza in Cartelle</b><br>\r\nIn quale Cartella dell\'archivio collaterale deve essere inserito questo\r\nFrammento?\r\n<p><b>Frammento</b><br>\r\nComincia a scrivere! O meglio ancora, copia il frammento da qualche altro\r\ndocumento elettronico e incollalo qui.</p>', 1045770409);
