insert into international (internationalId,languageId,namespace,message,lastUpdated) values (834,8,'WebGUI','Puoi caricare qualsiasi tipo di file nell\'archivio in modo da poterlo usare in\r\nseguito.\r\n<p><b>Nome</b><br>\r\nDai a questo file un nome univoco da utilizzare in seguito per poterlo\r\nrichiamare.\r\n<p><b>Organizza in Cartelle</b><br>\r\nIn quale Cartella dell\'archivio collaterale deve essere inserito questo file?\r\n<p><b>File</b><br>\r\nSeleziona un file da caricare dal tuo hard disk.</p>\r\n', 1045769858);
