insert into international (internationalId,languageId,namespace,message,lastUpdated) values (74,8,'Poll','Le seguenti sono le variabili disponibili nel template sondaggio: \r\n<p><b>canVote</b><br>\r\nUna condizione che indica se l\'utente ha i diritti per votare in questo\r\nsondaggio.</p>\r\n<p><b>question</b><br>\r\nLa domanda del sondaggio.\r\n<p><b>form.start</b><br>\r\nL\'inizio del form per votare.\r\n<p><b>answer_loop</b><br>\r\nUn loop contenente le informazioni relative alle domande nel sondaggio.\r\n<p>&nbsp;\r\n<blockquote>\r\n  <b>answer.form</b><br>\r\n  Il radio button per questa risposta.\r\n  <p><b>answer.text</b><br>\r\n  Il testo della risposta.\r\n  <p><b>answer.number</b><br>\r\n  il numero di questa risposta.&nbsp; 1, 2, 3, etc.\r\n  <p><b>answer.graphWidth</b><br>\r\n  La larghezza che avr il grafico per questa risposta. Basata su una\r\n  percentuale della dimensione totale del grafico.\r\n  <p><b>answer.percent</b><br>\r\n  La percentuale di voti che questa risposta ha ottenuto.\r\n  <p><b>answer.total</b><br>\r\n  Il numero totale di voti che questa risposta ha ricevuto.\r\n  <p>&nbsp;</p>\r\n</blockquote>\r\n<b>form.submit</b><br>\r\nIl bottone di invio per il form del sondaggio.\r\n<p><b>form.end</b><br>\r\nLa fine del form del sondaggio.\r\n<p><b>responses.label</b><br>\r\nL\'etichetta&nbsp; per il totale delle risposte. "Totale Voti"\r\n<p><b>responses.total</b><br>\r\nIl numero totale di voti che sono stati effettuati in questo sondaggio.</p>\r\n', 1054478853);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=937;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (937,8,'WebGUI','Per importare un tema hai bisogno di un file di tema valido esportato da un\r\naltro sito con WebGUI. Seleziona il tema dal tuo hard disk e clicca sul bottone\r\n"Importa"Ora ti sar mostrata una schermata di conferma che ti\r\nchieder se questo  il tema che vuoi importare. Se  questo, clicca ancora\r\nsul bottone "Importa" e avrai il il tuo nuovo tema. Adesso puoi\r\ncominciare ad applicare il tema al tuo sito proprio come faresti con un normale\r\nstile, template o elemento dell\'archivio collaterale.\r\n<p>Non puoi importare un tema due volte. Se desideri importare una nuova\r\nversione di un tema, allora devi prima cancellare la versione precedente del\r\ntema.\r\n<p>Allo stesso modo non puoi importare un tema creato da una versione di WebGUI\r\npi recente di quella che stai usando. Per questo se stai usando WebGUI 5.2.4 e\r\nil tema  stato creato con WebGUI 6.0.0, non ti sar possibile importare quel\r\ntema fino a quando non avrai aggiornato la versione di WebGUI.</p>\r\n', 1054477866);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=939;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (939,8,'WebGUI','<p>Quando cancelli un tema che hai creato, stai cancellando le propriet base\r\ndel tema. Comunque quando cancelli un tema che hai importato, stai cancellando\r\nanche tutti i suoi componenti ( stili, template e elementi dell\'archivio\r\ncollaterale). Assicurati che non dovrai pi usare quei componenti prima di\r\ncancellarli.</p>', 1054477122);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=958;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (958,8,'WebGUI','Gli Appunti sono un deposito particolare in cui vengono temporaneamente\r\narchiviati i contenuti &quot;tagliati&quot; o &quot;copiati&quot;. Gli elementi\r\nnegli Appunti&nbsp; possono poi essere incollati in una nuova posizione. \r\n<p>Gli elementi negli Appunti&nbsp; possono gestiti singolarmente. </p>\r\n<p>Puoi &quot;cancellare&quot; o &quot;incollare&quot; un elemento tramite il\r\npulsante appropriato. Puoi anche eliminare tutto il contenuto degli appunti\r\nspostandolo nel cestino tramite l\'apposita opzione nel&nbsp; men degli\r\nAppunti.</p>\r\n<p><b>Titolo</b><br>\r\nIl nome dell\'elemento negli Appunti. Puoi visualizzare questo elemento\r\nselezionando il titolo.\r\n<p><b>Tipo</b><br>\r\nIl tipo di contenuto. Per esempio una Pagina, un Articolo, un Calendario Eventi,\r\netc.\r\n<p><b>Data Appunti</b><br>\r\nLa data e l\'ora in cui l\'elemento  stato messo negli Appunti.\r\n<p><b>Posizione Precedente</b><br>\r\nLa posizione (nel sito) da cui  stato preso l\'elemento. Puoi vedere la\r\nposizione precedente selezionando posizione precedente.\r\n<p><b>Nome utente</b><br>\r\nIl nome utente di chi ha inserito l\'elemento negli appunti. Questo campo\r\nopzionale  visibile solamente negli Appunti Condiviso o quando un\r\namministratore sta gestendo gli Appunti di Sistema .</p>\r\n', 1054473476);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=961;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (961,8,'WebGUI','Il cestino  un deposito particolare in cui vengono temporaneamente archiviati\r\ni contenuti cancellati. Gli elementi nel cestino possono essere gestiti\r\nsingolarmente. Puoi &quot;tagliare&quot; un elemento negli Appunti oppure\r\neliminarlo permanentemente tramite il pulsante appropriato. Puoi anche eliminare\r\ntutto il contenuto del cestino tramite l\'apposita opzione nel&lt; men del\r\ncestino.\r\n<p><b>Titolo</b><br>\r\nIl nome dell\'elemento nel cestino. Puoi visualizzare questo elemento\r\nselezionando il titolo.\r\n<p><b>Tipo</b><br>\r\nil tipo di contenuto. Per esempio una Pagina, un Articolo, un Calendario Eventi,\r\netc.\r\n<p><b>Data Cestino</b><br>\r\nLa data e l\'ora in cui l\'elemento  stato messo nel cestino.\r\n<p><b>Posizione Precedente</b><br>\r\nLa posizione (nel sito) in cui si trovava precedentemente l\'elemento. Puoi\r\nvedere la posizione precedente selezionando posizione precedente.\r\n<p><b>Nome utente</b><br>\r\nIl nome utente di chi ha spostato l\'elemento nel cestino Questo campo opzionale\r\n visibile solamente nel Cestino Condiviso o quando un amministratore sta\r\ngestendo il Cestino di Sistema .</p>\r\n', 1054472919);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=934;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (934,8,'WebGUI','Creare e modificare i Temi  un processo semplice. Prima imposta alcune\r\npropriet di base per il Tema poi comincia ad aggiungere i componenti al Tema.\r\n<p>Le propriet di base del Tema non possono essere modificate da nessuno\r\ntranne che sul sito nel quale il tema  stato creato. <br>\r\nLe seguenti sono le definizioni delle proprit di base del Tema:\r\n<p><b>ID Tema</b><br>\r\nL\' ID univoco per questo tema all\'interno di questo sito. Questo ID cambier se\r\nquesto tema sar importato in un altro sito.\r\n<p><b>Nome Tema</b><br>\r\nQuesto  il nome univoco del tema. Deve essere univoco in ogni sito in cui il\r\ntema sar importato. Questo nome non cambier importandolo da un sito\r\nall\'altro \r\n<p><b>Designer del Tema</b><br>\r\nIl nome della persona o azienda che ha creato questo tema.\r\n<p><b>URL del Designer</b><br>\r\nLa URL della persona o azienda che ha creato questo tema. </p>\r\n', 1054394992);
