insert into international (internationalId,languageId,namespace,message,lastUpdated) values (72,8,'DataForm','Puoi aggiungere tutti i campi che vuoi al tuo modulo dati.\r\n<br>\r\n\r\n<br>\r\n<b>\r\nNome/Etichetta<br>\r\n</b>\r\nE\' un testo che dice all\'utente cosa questo campo rappresenta.</p>\r\n<b>\r\nNome Campo<br>\r\n</b>\r\nIl nome di questo campo.  Deve essere unico nell\'ambito di tutti gli altri campi del form (in pratica ogni campo deve avere un nome univoco).</p>\r\n<b>\r\nSottotitolo / Subtext<br>\r\n</b>\r\nE\' una descrizione di che cosa dovrebbe essere inserito nel campo. </p>\r\n<b>\r\n<p>Status<br>\r\n</b>\r\nI campi nascosti non saranno visibili al visitatore, ma il loro contenuto sar spedito nel messaggio.<br>\r\nI campi visualizzabili possono essere visti dal visitatore, ma non modificati.<br>\r\nI campi modificabili possono essere compilati dal visitatore.<br>I campi obbligatori devono essere compilati.\r\nSe utilizzi campi nascosti o visualizzabili assicurati di aver inserito una valore di default.</p><b>\r\n<p>Tipo<br></b>\r\nScegli il tipo di elemento da assegnare a questo campo. <p>\r\n\r\n<b>larghezza<br>\r\n</b>\r\nInserisci il numero di caratteri per determinare la larghezza di questo campo.</p>\r\n\r\n<b>Altezza<br>\r\n</b>\r\nInserisci il numero di caratteri per determinare l\'altezza. Solo per textarea e htmlarea</p>\r\n\r\n<b>Valori Possibili<br>\r\n</b>\r\nQuesto campo serve solo per il Men a tendina e i checkbox. Inserisci i valori che vuoi che vengano mostrati nel Men a tendina, uno per linea.</p>\r\n<b>\r\n<p>Valore di Default (opzionale)<br>\r\n</b>\r\ninserisci il valore di default (se vuoi) per il campo.  Per i campi si/No, inserisci "si" per spuntare "si" e "no" per spuntare "No".\r\n<p>\r\n\r\n\r\n\r\n', 1054393491);
