insert into international (internationalId,languageId,namespace,message,lastUpdated) values (89,8,'DataForm','Queste sono le variabili disponibili per il template List del Modulo Dati:\r\n<p><b>back.url</b><br>\r\nLa URL per tornare alla pagina di inserimento dati del Modulo.\r\n<p><b>back.label</b><br>\r\nL\'eticetta di default per il back.url.\r\n<p><b>field_loop</b><br>\r\nUn loop contenente le informazioni sui campi di questo Modulo.\r\n<p <blockquote><b>field.name</b><br>\r\nIl &quot;web safe name&quot; di questo campo.\r\n<p><b>field.label</b><br>\r\nL\'etichetta &quot;human readable&quot; per questo campo.\r\n<p><b>field.id</b><br>\r\nUn identificatore univoco che rappresenta questo campo nel database.\r\n<p><b>field.isMailField</b><br>\r\nUna condizione che indica se questo campo esiste per il &quot;sistema mail&quot;\r\ndel modulo dati.\r\n<p><b>field.type</b><br>\r\nIl tipo di dato associato a questo campo.\r\n<p>&nbsp;\r\n<blockquote>\r\n</blockquote>\r\n<b>record_loop</b><br>\r\nUn loop contenente i record inseriti in questo modulo dati.\r\n<p>&nbsp;\r\n<blockquote>\r\n  <b>record.entryId</b><br>\r\n  Un identificatore univoco per questo inserimento.\r\n  <p><b>record.ipAddress</b><br>\r\n  L\' IP Address che ha inviato questo inserimento.\r\n  <p><b>record.edit.url</b><br>\r\n  La URL per modificare questo record.\r\n  <p><b>record.username</b><br>\r\n  La username della persona che ha inviato questo inserimento.\r\n  <p><b>record.userId</b><br>\r\n  La user id della persona che ha inviato questo inserimento.\r\n  <p><b>record.submissionDate.epoch</b><br>\r\n  La epoch datestamp per questo inserimento.\r\n  <p><b>record.submissionDate.human</b><br>\r\n  La &quot;human readable date stamp&quot;, basata sulle preferenze dell\'utente,\r\n  per questo inserimento.\r\n  <p><b>record.data_loop</b><br>\r\n  Un loop contenente i dati inviati dall\'utente per ogni campo di questo modulo\r\n  dati.\r\n  <p>&nbsp;\r\n  <blockquote>\r\n    <b>record.data.value</b><br>\r\n    Il valore inviato dall\'utente per questo campo in questo inserimento.\r\n    <p><b>record.data.name</b><br>\r\n    Il &quot;web safe name&quot; di questo campo.\r\n    <p><b>record.data.label</b><br>\r\n    L\'etichetta &quot;human readable&quot; per questo campo.\r\n    <p><b>record.data.isMailField</b><br>\r\n    Una condizione che indica se questo campo esiste per il &quot;sistema\r\n    mail&quot; del modulo dati.\r\n  </blockquote>\r\n</blockquote>\r\n', 1054387767);
