insert into international (internationalId,languageId,namespace,message,lastUpdated) values (638,8,'WebGUI','I Templates sono usati per determinare come devono essere visualizzate le pagine. Attualmente i siti presentano molto di pi di un semplice men e di una grande area col contenuto. Molti di essi hanno tre o quattro colonne con magari una testata con banner etc. Si possono creare questi layout complessi utlizzando appunto i Templates. Ci sono diversi template predefiniti per semplificare il tuo lavoro, ma comunque puoi crearne quanti ne vuoi in modo che il tuo sito appaia esattamente come lo desideri.', 1054314102);
