insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,8,'LinkList','La seguente  la lista delle variabili disponibili nel template Lista di Link.\r\n<p><b>addlink.url</b><br>\r\nLa URL per aggiungere un link&nbsp; a questa Lista di Link.\r\n<p><b>addlink.label</b><br>\r\nL\'etichetta tradotta per la URL aggiungi link.\r\n<p><b>canEdit</b><br>\r\nUna condizione che indica se l\'utente pu modificare il link.<p><b>link_loop</b><br>\r\nQuesto loop contiene tutte le informazioni su ogni link.\r\n<blockquote>\r\n  <b>link.url</b><br>\r\n  La URL per questo link.\r\n  <p><b>link.name</b><br>\r\n  Il testo da linkare.\r\n  <p><b>link.controls</b><br>\r\n  I controlli di gestione di WebGUI per questo link.\r\n  <p><b>link.newwindow</b><br>\r\n  Una condizione che indica se il link deve essere aperto in una nuova finestra.\r\n  <p><b>link.description</b><br>\r\n  La description di questo link.\r\n', 1054313905);
