insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,8,'SiteMap','La seguente  la lista delle variabili disponibili nel template per la Mappa\r\ndel Sito.\r\n<p><b>page_loop</b><br>\r\nQuesto ciclo contiene tutto delle pagine della mappa del sito.\r\n<blockquote>\r\n  <b>page.indent</b><br>\r\n  Lo spazio di rientro per questa pagina che indica la profondit della pagina\r\n  nell\'albero di navigazione.\r\n  <p><b>page.url</b><br>\r\n  La URL alla pagina.\r\n  <p><b>page.id</b><br>\r\n  L\'identificatore unico che WebGUI usa internamente, per questa pagina.\r\n  <p><b>page.title</b><br>\r\n  Il titolo di questa pagina.\r\n  <p><b>page.menutitle</b><br>\r\n  Il titolo di questa pagina che appare nel men.\r\n  <p><b>page.synopsis</b><br>\r\n  La descrizione del contenuto di questa pagina (se esiste).\r\n  <p><b>page.isRoot</b><br>\r\n  Una condizione che indica se questa pagina  o non  una root.\r\n\r\n<p><b>page.isTop</b><br>\r\nUna condizione che indica se questa pagina  o meno alla sommit dell\'albero di navigazione.', 1048096405);
