insert into international (internationalId,languageId,namespace,message,lastUpdated) values (50,8,'Product','I benefici sono il vantaggio che offrono le caratteristiche del prodotto. Sono la ragione per cui il tuo prodotto  buono. Se aggiungi dei benefici, dovresti inserire anche qualche caratteristica.\r\n\r\n\r\n\r\n<p>\r\n\r\n<b>Beneficio</b><br>\r\nPuoi inserire un nuovo beneficio, selezionarne uno da quelli gi inseriti.\r\n<p>\r\n\r\n<b>Aggiungi un\'altro beneficio?</b><br>\r\nSe vuoi aggiungere un\'altro beneficio, seleziona "Si".\r\n<p>\r\n', 1046633484);
