insert into international (internationalId,languageId,namespace,message,lastUpdated) values (417,8,'WebGUI','<h1>Violazione della Sicurezza</h1>\r\nHai cercato di accedere ad un wobject non associato a questa pagina. Questo incidente  stato registrato.', 1046632861);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=162;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (162,8,'WebGUI','Sei sicuro di voler cancellare tutte le pagine e i wobjects nel cestino?', 1046632830);
delete from international where languageId=8 and namespace='LinkList' and internationalId=71;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'LinkList','Le liste di link sono semplicemente quello che pensi, una lista di link. Molti siti hanno una sezione di link, e questo wobject automatizza il processo.\r\n<br><br>\r\n<b>Template</b><br>\r\nScegli un template per questo Wobject\r\n<br><br>\r\n\r\n<b>Prossima Azione?</b><br>\r\nLascia selezionato a default se vuoi aggiungere link alla lista direttamente dopo averla creata.\r\n<br><br>\r\n', 1046632754);
delete from international where languageId=8 and namespace='FAQ' and internationalId=71;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'FAQ','Sembra che quasi ogni sito web , intranet e extranet nel mondo abbia una sezione FAQ (Frequently Asked Question). Questo wobject ti aiuta a costruirne una.\r\n<br><br>\r\n\r\n<b>Template</b><br>\r\nSeleziona un layout per questa FAQ.\r\n<br><br>\r\n<b>Continua aggiungendo una domanda?</b><br>\r\nSeleziona "aggiungi una domanda" se vuoi aggiungere domande alla FAQ dopo questa.\r\n<br><br>\r\n<hr size=1>\r\n<i><b>Nota:</b></i> IL seguente stile  specifico del wobject FAQ.\r\n<br><br>\r\n<b>.faqQuestion</b><br>\r\nUna domanda della F.A.Q. Per distinguerla dalla risposta.\r\n\r\n', 1046632667);
delete from international where languageId=8 and namespace='EventsCalendar' and internationalId=71;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'EventsCalendar','I calendari di eventi sono usati in molte intranet per tenere traccia di date interne che coinvolgono l\'intera organizzazione. I calendari di eventi sono anche, su siti per la clientela, un buon modo per informare i clienti su quali eventi si stanno realizzando o su che promozioni sono attualmente in corso.\r\n<br><br>\r\n<b>Template principale</b><br>\r\nSeleziona un template questo calendario eventi?\r\n<br><br>\r\n<b>Template evento</b><br>\r\nSeleziona un template per il singolo evento dentro al calendario\r\n<p><b>Mese di inzio</b><br>\r\nScegli il mese di inizio per il calendario. Se selezioni &quot;Corrente&quot; il\r\ncalendario partir sempre dal mese corrente, percui ogni mese cambier. Se\r\nselezioni &quot;Primo nel Calendario&quot; partir con la prima data\r\ndisponibile nel calendario.<br>\r\n<br>\r\n<b>Mese di fine</b><br>\r\nScegli il mese di fine per il calendario. Se selezioni &quot;Mostra X mesi\r\ndall\'inizio&quot;, solo X mesi di informazioni saranno mostrate. Se selezioni\r\n&quot;corrente&quot; il calendario finir nel mese in cui sei. Se selezioni\r\n&quot;ultimo nel calendario&quot; allora terminer alla data dell\'ultimo\r\ninserimento nel calendario.<br>\r\n<br>\r\n<b>Default Month</b><br>\r\nSeleziona con quale mese il calendario dovr partire quando un visitatore entra\r\nnella pagina che lo contiene.<br><br>\r\n<b>Cambio pagina dopo</b><br>\r\nQuando si usa il layout a lista, quanti eventi vuoi che vengano visualizzati per ogni pagina?\r\n<br><br>\r\n<b>Continua aggiungendo un evento?</b><br>\r\nLascia questo box selezionato se vuoi aggiungere eventi al calendario direttamente dopo averlo\r\ncreato.\r\n<br><br>\r\n<br><br>\r\n<i>Nota:</i> Gli eventi che sono gi accaduti non sono visualizzati nel calendario eventi.\r\n<br><br>\r\n', 1046632373);
delete from international where languageId=8 and namespace='ExtraColumn' and internationalId=71;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'ExtraColumn','Le colonne extra ti consentono di cambiare il layout della pagina per una pagina solamente. Se vuoi avere colonne multiple su tutte le pagine forse  meglio alterare lo <i>stile</i> applcato alle pagine. \r\n<br><br>\r\nLe colonne sono sempre aggiunte da sinistra a destra. Quindi ogni contenuto esistente sar piazzato alla sinistra della nuova colonna.\r\n<br><br>\r\n<b>Posizione</b><br>\r\nle colonne extra saranno sempre inserite da sinistra a destra.\r\n<br><br>\r\n<b>Spaziatore</b><br>\r\nLo spaziatore  la quantit di spazio tra il contenuto esistente e la tua nuova colonna. E\' misurato in pixel.\r\n<br><br>\r\n<b>Larghezza</b><br>\r\nLa larghezza della nuova colonna da aggiungere. La larghezza  misurata in pixel.\r\n<br><br>\r\n<b>StyleSheet Class</b><br>\r\nPer default  applicato lo stile <i>content</i> (che  lo stile usato dal corpo di tutto il sito). Comunque, se hai creato uno stile specifico per le colonne, puoi modificare questa classe.\r\n', 1046629104);
delete from international where languageId=8 and namespace='Product' and internationalId=63;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (63,8,'Product','La lista delle specifiche associate a questo prodotto.\r\n<blockquote>\r\n  <b>specification.controls</b><br>\r\n  I controlli di gestione di WebGUI&nbsp; per questa specifica.\r\n  <p><b>specification.specification</b><br>\r\n  Il testo stesso della specifica.\r\n  <p><b>specification.units</b><br>\r\n  L\'unit di misura per questa specifica. ex: metri\r\n  <p><b>specification.label</b><br>\r\n  L\'etichetta per questa specifica. ex: altezza\r\n  <p>&nbsp;</p>\r\n</blockquote>\r\n<p><b>addaccessory.url</b><br>\r\nLa URL per aggiungere un accessorio a questo prodotto.\r\n<p><b>addaccessory.label</b><br>\r\nL\'etichetta tradotta per il link per aggiungere un accessorio.\r\n<p><b>accessory_loop</b><br>\r\nQuesto ciclo contiene tutti gli accessori associati&nbsp; a questo prodotto.\r\n<blockquote>\r\n  <b>accessory.url</b><br>\r\n  La URL all\'accessorio linkato.\r\n  <p><b>accessory.title</b><br>\r\n  Il titolo dell\'accessorio linkato.\r\n  <p><b>accessory.controls</b><br>\r\n  I controlli di gestione di WebGUI&nbsp; per questo accessorio.\r\n  <p>&nbsp;</p>\r\n</blockquote>\r\n<p><b>addRelatedProduct.url</b><br>\r\nla URL per aggiungere un prodotto correlato a questo prodotto.\r\n<p><b>addRelatedProduct.label</b><br>\r\nL\'etichetta tradotta per il link per aggiungere un prodotto correlato .\r\n<p><b>relatedproduct.url</b><br>\r\nLa URL al prodotto linkato.\r\n<p><b>relatedproduct.title</b><br>\r\nIl titolo del prodotto linkato.\r\n<p><b>relatedproduct.controls</b><br>\r\nI controlli di gestione di WebGUI&nbsp; per questo prodotto correlato.</p>\r\n\r\n', 1046628858);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=607;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (607,8,'WebGUI','<b>Registrazione Anonima</b><br>\r\nVuoi abilitare i visitatori a registrarsi da soli al sito?\r\n<p><b>Esegui su Registrazione</b><br>\r\nSe qui inserisci una linea di comando, sar eseguita ogni volta che un utente\r\nsi registra.</p>\r\n<p><b>Abilita Karma?</b><br>\r\nVuoi abilitare la funzione Karma?</p>\r\n<p><b>Karma per Login</b><br>\r\nLa quantit di&nbsp; Karma che viene accreditato ad ogni utente quando effettua\r\nil login. Ha effetto solo se il karma  abilitato.</p>\r\n<p>\r\n\r\n\r\n<b>Session Timeout</b><br>\r\nIl tempo (in secondi) che una sessione utente rimane attiva (prima che sia necessario un nuovo login). Il timeout si azzera ogni volta che si clicca una pagina. Quindi se setti il timeout per 8 ore, un utente dovr rifare il log in non ha visitato il sito the site da 8 ore.</p>\r\n<p>\r\n\r\n<b>Consenti agli utenti di disattivare i propri account?</b><br>\r\nConsenti ai tuoi utenti di disattivare i propri account senza un tuo intervento?<br>\r\n<br>\r\n<b>Metodo di autenticazione (default)</b><br>\r\nQuale dovrebbe essere il metodo di autenticazione predefinito per i nuovi account creati? I due metodi possibili sono WebGUI e LDAP. L\'autenticazione WebGUI significa che gli utenti si autenticheranno in base alle informazioni conservate nel database. L\'autenticazione LDAP significa che gli utenti si autenticheranno in base ad un server LDAP sesterno.\r\n<br><br>\r\n<i>Nota:</i> I settaggi di autenticazione possono essere personalizzati utente per utente.\r\n<h2>WebGUI Authentication Options</h2>\r\n<b>Invia un messaggio di benvenuto?</b><br>\r\nDesideri che WebGUI automaticamente invii un messaggio di benvenuto all\'utente\r\nquando si registra?\r\n<p><b>NOTA:</b> In aggiunta al tuo messaggio saranno incluse&nbsp; anche le\r\ninformazioni sull\'account.<p><b>Messaggio di Benvenuto</b><br>\r\nScrivi il messaggio che vuoi che venga inviato all\'utente quando si registra.<p><b>Recupera\r\nPassword - Messaggio</b><br>\r\nScrivi il messaggio che vuoi che venga inviato agli utenti se cercano di\r\nrecuperare la loro password di WebGUI .</p>\r\n<p>\r\n\r\n&nbsp;\r\n\r\n<h2>LDAP Authentication Options</h2>\r\n<br><br>\r\n<b>LDAP URL (default)</b><br>\r\nLa URL di default del server LDAP. La URL LDAP  nella forma <b>ldap://[server]:[port]/[base DN]</b>. Esempio:\r\nldap://ldap.mycompany.com:389/o=MyCompany.\r\n<p><b>LDAP Identity</b><br>\r\nLa LDAP  l\'identificatore unico nel LDAP server con cui l\'utente sar\r\nidentificato. Spesso questo campo  un <b>shortname</b>, che prende la forma\r\ndella&nbsp; iniziale del nome&nbsp; + il cognome. Esempio: mrossi. Per questo se\r\nspecifichi la LDAP identity per essere un&nbsp; <i>shortname</i> allora Mario\r\nRossi&nbsp; dovr inserire&nbsp; <i>mrossi</i> durante il processo di\r\nregistrazione.<br>\r\n<br>\r\n<b>LDAP Identity Name</b><br>\r\nL\'etichetta utilizzata per descrivere LDAP Identity all\'utente. Per esempio,\r\nalcune aziende usano un LDAP server per autenticare i loro utenti con proxy\r\nserver. Nella documentazione fornita ai propri utenti, la LDAP identity \r\nconosciuta come la loro <i>Web Username. Quindi per coerenza puoi inserire\r\nquesta etichetta.<br>\r\n<br>\r\n<b>LDAP Password Name</b><br>\r\nCome per LDAP Identity Name  una etichetta , cos lo  la LDAP Password\r\nName. Usa questa etichetta secondo gli stessi principi della LDAP Identity Name.</i></p>\r\n\r\n', 1046627458);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=620;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (620,8,'WebGUI','Come il nome suggerisce, con questa funzione cancellerai un gruppo e rimuoverai tutti gli utenti da quel gruppo. Stai attento a non lasciare orfani degli utenti delle pagine alle quali avrebbero accesso cancellando un gruppo che  ancora in uso.\r\n<p>\r\n\r\nCome per ogni operazione di cancellazione, sei chiamato a rispondere ad una conferma per procedere con la cancellazione. Se rispondi di s, la cancellazione proceder e non ci sar possibilit di recuperare i dati. Se rispondi di no sarai riportato alla schermata precedente.', 1046625755);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=623;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (623,8,'WebGUI','<a href="http://www.w3.org/Style/CSS/">Cascading Style Sheets (CSS)</a> sono lo strumento migliore per gestire il look and feel di un  web site. Sono largamente usati nel C.M.S.\r\n<p>\r\n\r\nQuella che segue  una lista di classi usate per gestire il look :\r\n<p>\r\n\r\n<b>A</b><br>\r\nI links nello stile.\r\n<p>\r\n\r\n<b>BODY</b><br>\r\nIl setup di default delle pagine nello stile.\r\n<p>\r\n\r\n<b>H1</b><br>\r\nGli headers di ogni pagina.\r\n<p>\r\n\r\n<b>.accountOptions</b><br>\r\nI links che appaiono in modalit login e account update forms.\r\n<p>\r\n\r\n<b>.adminBar </b><br>\r\nLa barra che appare in alto quando sei amministratore del sito.\r\n<p>\r\n\r\n<b>.content</b><br>\r\nLa principale area di contenuto in ogni pagina dello stile.\r\n<p>\r\n\r\n<b>.formDescription </b><br>\r\nI tags su ogni forms prossimi agli elementi del form. \r\n<p>\r\n\r\n<b>.formSubtext </b><br>\r\nI tags sotto alcuni elementi dei form.\r\n<p>\r\n\r\n<b>.highlight </b><br>\r\nDenota un elemento in evidenza, come quale messaggio stai visualizzando in una lista.\r\n<p>\r\n\r\n<b>.horizontalMenu </b><br>\r\nHorizontal menu (se stai usando una horizontal menu macro).\r\n<p>\r\n\r\n<b>.pagination </b><br>\r\nIl link alla pagina precedente o successiva se stai usando il cambia pagina dopo....\r\n<p>\r\n\r\n<b>.selectedMenuItem</b><br>\r\nUtilizza questa classe per evidenziare la pagina corrente in ognuno dei menu macro.\r\n<p>\r\n\r\n<b>.tableData </b><br>\r\nI contenuti di elementi come forum e contributi dagli utenti.\r\n<p>\r\n\r\n<b>.tableHeader </b><br>\r\nHead delle colonne di elementi come forum e contributi dagli utenti.\r\n<p>\r\n\r\n<b>.tableMenu </b><br>\r\nIl menu di elementi come forum e contributi dagli utenti.\r\n<p>\r\n\r\n<b>.verticalMenu </b><br>\r\nVertical menu (se stai utilizzando una vertical menu macro).\r\n<p>\r\n\r\n<i><b>Note:</b></i> Alcuni elementi e macro hanno il proprio stile univoco, che  documentato nello specifico file di help.\r\n\r\n', 1046622652);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=621;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (621,8,'WebGUI','Gli stili del C.M.S.  sono abilitati alle macro. Vedi <i>Usare le Macros</i> per maggori informazioni.\r\n<p>\r\n\r\n<b>Nome dello stile</b><br>\r\nUn nome univoco che descriva come appare lo stile in una parola. Il nome non ha effetto sulla visualizzazione dello stile.\r\n<p>\r\n\r\n<b>Body</b><br>\r\nBody rappresenta la visualizzazione del tuo sito. Copia il tuo codice HTML  e incollalo nello spazio apposito. \r\n<p>\r\n\r\nMolte persone aggiungeranno macro del C.M.S. ai loro body per automatizzare la navigazione, e per altre funzionalit.\r\n<p>\r\nEsiste una macro particolare, questa ^-; che dovrai necessariamente posizionare nella parte del body in cui vuoi che venga inserito il contenuto. Non dimenticare di mettere la macro ^AdminBar per visualizzare le funzioni amministrative.\r\n\r\n<p>\r\n\r\n<b>Style Sheet</b><br>\r\nMetti il codice del tuo foglio di stile qui. Gli Style sheets, o fogli di stile sono usati per controllare colori, dimensioni e altre propriet degli elementi del tuo sito. Vedi <i>Usare gli Style Sheets</i> per maggiori informazioni.\r\n<p>\r\n\r\n<i>Utenti avanzati:</i> per una maggior performance create gli style sheet sul file system (chiamateli tipo tuosito.css) e aggiungete in questa area un link come questo: \r\n&lt;link href="/tuosito.css" rel="stylesheet" rev="stylesheet" type="text/css"&gt;', 1046622576);
delete from international where languageId=8 and namespace='MessageBoard' and internationalId=74;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (74,8,'MessageBoard','Questo  l\'elenco delle variabili di template disponibili per i template dei\r\nForum.\r\n<p><b>canPost</b><br>\r\nUna condizione che indica se un utente pu aggiungere un nuovo messaggio.\r\n<p><b>post.url</b><br>\r\nLa&nbsp; URL per aggiungere un nuovo messaggio.\r\n<p><b>post.label</b><br>\r\nL\'etichetta tradotta per il link da cui postare.\r\n<p><b>search.url</b><br>\r\nLa URL per cercare in questo forum.\r\n<p><b>search.label</b><br>\r\nL\'etichetta tradotta per il link al&nbsp; search engine.\r\n<p><b>subject.label</b><br>\r\nL\'etichetta tradotta per&nbsp; la colonna Oggetto.\r\n<p><b>user.label</b><br>\r\nL\'etichetta tradotta per&nbsp; la colonna Utente\r\n<p><b>date.label</b><br>\r\nL\'etichetta tradotta per&nbsp; la colonna Data.\r\n<p><b>views.label</b><br>\r\nL\'etichetta tradotta per&nbsp; la colonna Visualizzato.\r\n<p><b>replies.label</b><br>\r\nL\'etichetta tradotta per&nbsp; la colonna Risposte.\r\n<p><b>last.label</b><br>\r\nL\'etichetta tradotta per&nbsp; la colonna Ultima Risposta\r\n<p><b>message_loop</b><br>\r\nUn ciclo che contiene ogni messaggio.\r\n<blockquote>\r\n  <b>last.url</b><br>\r\n  La URL all\'ultima risposta a questo messaggio.\r\n  <p><b>last.subject</b><br>\r\n  L\'oggetto dell\'ultima risposta a questo messaggio.\r\n  <p><b>last.username</b><br>\r\n  Il Nome Utente di chi&nbsp; ha postato l\'ultima risposta a questo messaggio.\r\n  <p><b>last.userProfile</b><br>\r\n  La URL&nbsp; al profilo dell\'utente che ha postato l\'ultima risposta a questo\r\n  messaggio.\r\n  <p><b>last.date</b><br>\r\n  La data in cui l\'ultima risposta a questo messaggio  stata inviata.\r\n  <p><b>message.subject</b><br>\r\n  L\'oggetto di questo messaggio.\r\n  <p><b>message.date</b><br>\r\n  La data in cui questo messaggio  stato postato.\r\n  <p><b>message.user</b><br>\r\n  L\'utente che ha postato questo messaggio.\r\n  <p><b>message.views</b><br>\r\n  Le visualizzazioni che questo messaggio ha ricevuto.\r\n  <p><b>message.replies</b><br>\r\n  Il numero di risposte a questo thread.\r\n  <p><b>message.url</b><br>\r\n  La URL per visualizzare questo messaggio.\r\n  <p><b>message.currentUser</b><br>\r\n  Una condizione che indica se l\'utente attuale  lo stesso che ha postato\r\n  questo messaggio.\r\n  <p><b>message.status</b><br>\r\n  Lo stato di questo messaggio (pendente, approvato, rifiutato).\r\n  <p>&nbsp;</p>\r\n</blockquote>\r\n<p><b>firstPage</b><br>\r\nUn Link alla prima pagina dell\'impaginazione.\r\n<p><b>lastPage</b><br>\r\nUn Link all\'ultima pagina dell\'impaginazione.\r\n<p><b>nextPage</b><br>\r\nUn Link alla prossima pagina dell\'impaginazione.\r\n<p><b>previousPage</b><br>\r\nUn Link alla prossima pagina dell\'impaginazione.\r\n<p><b>pageList</b><br>\r\nUna lista di link a tutte le pagine dell\'impaginazione del calendario.\r\n<p><b>multiplePages</b><br>\r\nUna variabile che indica dove esiste pi di una pagina nell\'impaginazione.</p>\r\n', 1046621598);
