insert into international (internationalId,languageId,namespace,message,lastUpdated) values (637,8,'WebGUI','<b>Nome</b><br>\r\nIl nome dell\'utente.\r\n<p>\r\n\r\n<b>Secondo Nome</b><br>\r\nIl secondo nome dell\'utente.\r\n<p>\r\n\r\n<b>Cognome</b><br>\r\nIl cognome dell\'utente.\r\n<p>\r\n\r\n<b>Indirizzo Email</b><br>\r\nIndirizzo email dell\' utente. Deve essere inserito se l\' utente vuole utilizzare le funzioni che richiedono l\' email.\r\n<p>\r\n\r\n\r\n<b>Lingua</b><br>\r\nQuale lingua deve essere usata dal sistema per mostrare messaggi e indici.\r\n<p>\r\n\r\n<b>ICQ UIN</b><br>\r\n<a href="http://www.icq.com">ICQ</a> UIN  " ID numero utente" sul network ICQ. ICQ  una tra le pi popolari piattaforme di instant messaging.\r\n\r\n\r\n<b>Nota:</b> Puoi inserire tutti le categorie e i campi che desideri, puoi senderli obbligatori o meno. Nella gersione profilo troverai configurati quelli di default nella distribuzione di WebGUI.\r\n\r\n', 1046619284);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=618;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (618,8,'WebGUI','\r\n<b>SMTP Server</b><br>\r\nQuesto  l\'indirizzo del tuo mail server locale. E\' necessario per tutte le funzionalit che coinvolgono la posta internet (come la funzione di recupero password).<br><br>\r\n\r\n<b>Email footer</b><br>\r\nQuesto footer sar processato per le macro e allegato ad ogni email inviata da questa istanza di WebGUI.\r\n<br><br>\r\n<b>Avverti quando c\' un nuovo utente?</b><br>\r\nQualcuno deve essere avvisato se un nuovo utente si registra anonimamente?\r\n<br><br>\r\n<b>Gruppo da avvertire quando c\' un nuovo utente?</b><br>\r\nQuale gruppo deve essere avvisato se un nuovo utente si registra anonimamente?\r\n<br><br>\r\n\r\n', 1046556120);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=617;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (617,8,'WebGUI','I Settaggi sono opzioni che ti consentono di aggiustare il C.M.S. per i tuoi particolari bisogni.\r\n<p>\r\n\r\n\r\n<b>Modifica informazioni sull\'azienda</b><br>\r\nInformazioni specifiche riguardo l\'azienda o l\'individuo che controlla questa installazione del C.M.S. .\r\n<p>\r\n\r\n<b>Modifica i settaggi dei contenuti</b><br>\r\nSettaggi correlati a contenuti e alla loro gestione.\r\n<p>\r\n\r\n<b>Modifica i settaggi della messaggistica</b><br>\r\nSettaggi per le email e le funzioni relative alla messaggistica.\r\n<p>\r\n\r\n<b>Modifica settaggi vari</b><br>\r\nTutto il resto.\r\n\r\n<b>Modifica i settaggi del profilo</b><br>\r\nDefinisce come appaioni i profili degli utenti e cosa gli utenti hanno i privilegi di modificare<p>\r\n\r\n\r\n\r\n<b>Modifica i settaggi per gli utenti</b><br>\r\nQuesti settaggi riguardano la identificazione degli utenti e il login, etc.\r\n<p>\r\n\r\n', 1046555813);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=613;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (613,8,'WebGUI','Gli utenti nel sistema sono gli account ai quali  concesso fare determinate cose. Ci sono due utenti di default: Admin e Visitor.\r\n<p>\r\n\r\n<b>Admin</b><br>\r\nAdmin  esattamente quello che ti aspetti. E\' un utente con privilegi illimitati nell\'ambiente. Se qualcosa pu essere fatto, questo utente ha i diritti per farlo.\r\n<p>\r\n\r\n<b>Visitor</b><br>\r\nVisitor  esattamente l\'opposto di Admin. Visitor non ha nessun diritto. Per default qualsiasi utente non loggato  visto come utente Visitor.\r\n<p>\r\n\r\n<b>Aggiungi un nuovo utente.</b><br>\r\nClicca qui per andare allo schermo di aggiunta utente.\r\n<p>\r\n\r\n<b>Cerca</b><br>\r\nPuoi cercare gli utenti basandoti sullo username o sull\'indirizzo email. Puoi anche fare delle ricerche parziali se preferisci.', 1046555073);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=639;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (639,8,'WebGUI','<b>Nome Template</b><br>\r\nDai un nome descrittivo al template, cos lo riconoscerai quando vorrai applicarlo ad una pagina.\r\n<p>\r\n<b>Namespace</b><br>\r\nChe tipo di template  questo?\r\n<p>\r\n\r\n\r\n<b>Template</b><br>\r\nCrea il tuo template inserendo i comandi di template, le macro di variabile e il codice.\r\n<p>\r\n\r\n\r\n<b>Nota:</b>\r\nNon devi mai modificare i template di default che trovi nella distribuzione di WebGUI, perch possono variare con ogni realise per introdurre nuove funzioni. Devi fare una copia del template che ti interessa e modificare quello.\r\n', 1046550619);
delete from international where languageId=8 and namespace='SiteMap' and internationalId=71;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'SiteMap','Le mappe del sito sono usate per aggiungere un ulteriore ausilio nella navigazione. Puoi aggiungere una mappa del sito tradizionale che visualizzi una vista gerarchica di tutte le pagine nel sito. D\'altra parte, puoi usare le mappe del sito per dare un ausilio di navigazione a certi livelli nel sito.\r\n<br><br>\r\n\r\n<b>Template</b><br>\r\nSeleziona un layout per questa mappa.\r\n<br><br>\r\n<b>Parti Da</b><br>\r\nSeleziona la pagina da cui vuoi far iniziare la mappa.\r\n<br><br>\r\n<b>Partire da questo Livello?</b><br>\r\nQuanti livelli di profondit della navigazione deve mostrare la mappa? Se  specificato 0(zero) mostrer tutti i livelli esistenti.\r\n<br><br>\r\n\r\n<b>Rientro</b><br>\r\nDi quanti caratteri deve rientrare ogni livello?<br><br>\r\n\r\n', 1046550282);
delete from international where languageId=8 and namespace='SQLReport' and internationalId=71;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'SQLReport','I report SQL sono forse il pi potente tra i wobject. Consentono all\'utente di eseguire query su qualsiasi database al quale abbia accesso. Questo  utile ad esempio per estrarre dati dal tuo database degli account o per creare dei sommari di\r\ntutti i forum di discussione nel sito.<p>\r\n\r\n<b>Preprocessa le macro sulla quesry?</b><br>\r\nVuoi che vengano processate le macro del C.M.S.? Seleziona questo box.<p><b>Debug?<br>\r\n</b>Seleziona questo box se vuoi visualizzare il debugging e i messaggi di\r\nerrore nella pagina.<p><b>Query</b><br>\r\nQuesta  una query SQL standard. Puoi rendere pi dinamiche le tue query\r\nutilizzando la macro ^FormParam();&nbsp;\r\n<p>\r\n\r\n<b>Report Template</b><br>\r\nScrivi qui il codice per generare il report. normalmente userai delle tabelle HTML per generarlo. Sotto  incluso un esempio. Se lasci questo campo in bianco il template sar generato in base al risultato della query.\r\n<p>\r\n\r\nCi sono delle macro speciali per generare report SQL. Sono: ^-;, ^0;, ^1;, ^2;, ^3;, etc. Queste macro saranno processate comunque, a prescindere dal fatto che tu abbia deselezionato la box processa macro. La macro ^- rappresenta i punti di split nel documento dove il report comincia e finisce di fare il loop. Le macro numeriche rappresentano i dati ritornati dalla\r\nquery. C\' una macro aggiuntiva ^rownum; che conta le righe della query\r\npartendo da 1 per usarla dove le linee del\'output devono essere numerate. <p>\r\n\r\n<b>DSN</b><br>\r\n<b>D</b>ata <b>S</b>ource <b>N</b>ame  l\'identificatore univoco che il linguaggio PERL usa per descrivere la locazione del tuo database. La sintassi  DBI:[driver]:[database name]:[host]. \r\n<p>\r\n\r\n<i>Esempio:</i> DBI:mysql:WebGUI:localhost\r\n<p>\r\n\r\n<b>Utente Database</b>\r\nLo username che usi per collegarti al DSN.\r\n<p>\r\n\r\n<b>Password Database</b>\r\nLa password che usi pe rcollegarti al DSN.\r\n<p>\r\n\r\n<b>Pagina dopo</b>\r\nQuante righe vuoi che siano visualizzate prima di dividere il risultato in pagine separate? In altre parole, quante righe vuoi che siano visualizzate per pagina?\r\n<p>\r\n\r\n<b>Converti gli a capo?</b>\r\nVuoi convertire gli a capo nei dati del risultato in break HTML (&lt;br&gt;).\r\n', 1046549982);
delete from international where languageId=8 and namespace='Product' and internationalId=43;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (43,8,'Product','Le specifiche sono le caratteristiche tecniche del prodotto.</p><p><b>\r\n\r\nTipo della specifica<br>\r\n</b>\r\nil tipo di specifica. Per esempio altezza, larghezza, o colore. Puoi selezionarne una gi inserita nel sistema per un altro prodotto, oppure inserirne una nuova.</p><p><b>\r\n\r\nSpecifica<br>\r\n</b>\r\nil valore della specifica. Per esempio, se hai scelto altezza come tipo dovrai inserire un valore numerico come "12".</p><p><b>\r\n\r\nUnit<br>\r\n</b>\r\nLa unit di misura per questa specifica.  Per esempio, se hai scelto altezza come tipo, probabilmente dovrebbe essere "centimetri".</p><p><b>\r\n\r\nAggiungi una nuova specifica?<br>\r\n</b>\r\nSe vuoi aggiungere una ulteriore specifica, seleziona "Si".\r\n\r\n\r\n\r\n\r\n', 1046548417);
delete from international where languageId=8 and namespace='Product' and internationalId=39;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (39,8,'Product','Il C.M.S. comprende un sistema di gestione catalogo prodotti che consente di pubblicare facilmente e velocemente i tuoi prodotti e servizi. \r\n<br><br>\r\n<p><b>Template</b><br>\r\nSeleziona un layout per questo prodotto.</p>\r\n\r\n<b>Prezzo</b><br>\r\nIl prezzo di questo prodotto. Se preferisci puoi lasciarlo vuoto oppure inserire un messaggio tipo: contattaci per informazioni.<p><b>\r\n\r\nCodice Prodotto<br>\r\n</b>\r\nIl codice del prodotto, o un identificativo del prodotto.</p><p><b>\r\n\r\nImmagine Prodotto 1<br>\r\n</b>\r\nUna immagine di questo prodotto.</p><p><b>\r\n\r\nImmagine Prodotto 2<br>\r\n</b>\r\nUna immagine di questo prodotto.</p><p><b>\r\n\r\nImmagine Prodotto 3<br>\r\n</b>\r\nUna immagine di questo prodotto.</p><p>\r\n<p><b>Brochure</b><br>\r\nLa brochure per questo prodotto.\r\n<p><b>Manuale</b><br>\r\nIl manuale di utilizzo per questo prodotto.\r\n<p><b>Garanzia</b><br>\r\nLa garanzia per questo prodotto.</p>\r\n', 1046548367);
delete from international where languageId=8 and namespace='LinkList' and internationalId=73;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,8,'LinkList','<b>Titolo</b><br>\r\nIl testo che apparir nel link<p>\r\n<b>URL</b>\r\n<br>Il sito da linkare<p>\r\n<b>Apri in nuova finestra?</b><br>\r\nSeleziona "si" se vuoi che questo link si apra in una finestra nuova.\r\n<p><b>Descrizione</b><br>\r\nDescrivi il sito che stai linkando. Pu essere omessa se vuoi.<p>\r\n<b>Prossima Azione?</b><br>\r\nLascia selezionato a default se vuoi aggiungere link alla lista.\r\n<br><br>\r\n', 1046547071);
delete from international where languageId=8 and namespace='FileManager' and internationalId=73;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,8,'FileManager','<b>Titolo del file</b><br>\r\nIl titolo che sar mostrato per questo file. Se lasciato vuoto sar visualizzato il nome del file \r\n<p>\r\n\r\n<b>File</b><br>\r\nScegli dal tuo computer il file che vuoi caricare.\r\n<p>\r\n\r\n<b>Versione Alternativa #1</b><br>\r\nUna versione alternativa del file ad esempio se il 1 file era una immagine jpg la versione alternativa potrebbe essere un file tiff oppure un file bmp.\r\n<p>\r\n\r\n<b>Versione Alternativa #2</b><br>\r\nUna versione alternativa del file ad esempio se il 1 file era una immagine jpg la versione alternativa potrebbe essere un file tiff oppure un file bmp.\r\n<p>\r\n\r\n<b>Breve descrizione</b><br>\r\nUna breve descrizione del file. Assicurati di includere delle Keyword adatte, i visitatori potrebbero tentare una ricerca.\r\n<p>\r\n\r\n<b>Gruppo di Download</b><br>\r\nScegli il gruppo che pu scaricare il file.\r\n<p>\r\n\r\n<b>Prossima Azione?</b><br>\r\nSe hai un altro file da aggiungere allora seleziona "Aggiungi un nuovo file", diversamente seleziona "Torna alla pagina".', 1046546766);
delete from international where languageId=8 and namespace='FileManager' and internationalId=71;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,8,'FileManager','Il File Manager  pensato per aiutarti a gestire la distribuzione di file sul tuo sito. Ti consente di specificare chi pu\r\nvisualizzare/scaricare i files dal tuo sito.<p>\r\n<b>Template</b><br>\r\nScegli un layout per il file manager.\r\n<br><br>\r\n\r\n<b>Cambio pagina dopo</b><br>\r\nQuanti file devono essere visualizzati prima di separare i risultati in pagine separate? In altre parole, quanti file vuoi che siano visualizzati per pagina?\r\n<p>\r\n\r\n<b>Continua aggiungendo un file?</b><br>\r\nSe vuoi cominciare ad aggiungere files per il download subito dopo aver creato il Download Manager, lascialo selezionato.\r\n<p>', 1046546498);
