insert into international (internationalId,languageId,namespace,message,lastUpdated) values (619,8,'WebGUI','Questa funzione cancella permanentemente il wobject selezionato da una pagina. Se non sei sicuro di voler cancellare questo contenuto faresti meglio a tagliare (cut) il contenuto negli appunti finch non sarai sicuro di volerlo veramente cancellare.\r\n<p>\r\n\r\nCome con ogni operazione di cancellazione, sei chiamato a rispondere ad una conferma per procedere con la cancellazione. Se rispondi di s, la cancellazione proceder e non ci sar possibilit di recuperare i dati. Se rispondi di no sarai riportato alla schermata precedente.', 1046126787);
