insert into international (internationalId,languageId,namespace,message,lastUpdated) values (625,8,'WebGUI','Carica tutte le immagini che potresti voler utilizzare in pi di una posizione sul sito.\r\n<p>\r\n<b>Nome</b><br>\r\nL\'etichetta con la quale questa immagine verr chiamata per l\'inclusione nelle pagine.\r\n<p>\r\n\r\n<b>Organizza in Cartelle</b><br>\r\nLa cartella Dell\'Archivio Collaterale nella quale deve essere inserita questa immagine.\r\n<p>\r\n\r\n<b>File</b><br>\r\nSeleziona un file dal tuo disco rigido da uploadare sul server.\r\n<p>\r\n\r\n<b>Parametri</b><br>\r\nAggiungi qualsiasi parametro HTML &ltimg&rt; che vuoi come default per questa immagine.\r\n<p>\r\n<i>Esempio:</i><br>\r\nalign="right"\r\nalt="Questa  una immagine"\r\n\r\n<b>Dimensione Thumbnail</b><br>\r\nQuanti pixel deve misurare il thumbnail di questa immagine.\r\n<p>\r\n', 1046124685);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=635;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (635,8,'WebGUI','I Package sono gruppi di pagine e widgets che sono predefinite per essere sviluppate insieme. Un package manager pu sentire l\'esigenza di creare varie pagine con un forum, una FAQ, un sondaggio perch questa operazione viene eseguita spesso. I Packages sono definiti per diminuire il disturbo di azioni ripetitive.\r\n<br><br>\r\nUn package che la gente spesso crea  un package pagina/articolo. Spesso quando aggiungi una pagina vuoi anche aggiungere un articolo ad essa. Invece di fare queste operazioni una alla volta, creare la pagina, andare a quella pagina, e aggiungere l\'articolo alla pagina, troverai pi veloce creare un package che faccia tutte queste cose in un colpo solo.', 1046124350);
delete from international where languageId=8 and namespace='WebGUI' and internationalId=633;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (633,8,'WebGUI','I roots sono le pagine principali su cui si sviluppa il sito. La prima e la pi importante root  la "Home" page. Molti non hanno necessit di aggiungere una nuova root, ma a qualcuno potrebbe servire. Pu essere necessario creare nuove root per diverse ragioni, ad esempio per un sito in due lingue sar necessario creare due root: italiano e inglese.', 1046123933);
