insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,2,'Article','Die nachfolgenden Template Variablen sind f&uuml;r Artikel Templates verf&uuml;gbar.<P><B>new.template</B><BR>Artikel \r\nbieten die spezielle M&ouml;glichkeit, ein Template ausw&auml;hlen zu lassen, sodass Sie \r\nIhren Besuchern erlauben k&ouml;nnen, verschiedene Ansichten des Artikel anzeigen \r\nzu lassen. Und zwar wird dies folgendermassen gel&ouml;st: Sie f&uuml;gen einfach eine \r\nURL wie diese hinzu (ersetzen Sie die 999 durch die Template ID, die Sie benutzen \r\nm&ouml;chten):\r\n<P>&lt;a href=&quot;&lt;tmpl_var new.template&gt;999&quot;&gt;Lesen Sie mehr...&lt;/a&gt; \r\n<P><B>description.full</B><BR>Der komplette Text ohne Pagination  \r\n<P><B>description.first.100words</B><BR>Die ersten 100 W&ouml;rter. W&ouml;rter werden \r\ndefiniert als Zeichenfolgen getrennt durch ein Leerzeichen, sodass HTML Tags \r\nauch als Wort gez&auml;hlt werden.. \r\n<P><B>description.first.75words</B><BR>Die ersten 75 W&ouml;rter<P><B>description.first.50words</B><BR>Die \r\nersten 50 W&ouml;rter.<P><B>description.first.25words</B><BR>Die ersten 25 W&ouml;rter.<P><B>description.first.10words</B><BR>Die \r\nersten 10 W&ouml;rter.<P><B>description.first.paragraph</B><BR>Der erste Abschnitt. \r\nDieser wird definiert bis zum ersten Carriage Return, das im Text gefunden wird.<P><B>description.first.2paragraphs</B><BR>Die \r\nersten beiden Abschnitte.<P><B>description.first.sentence</B><BR>Der erste Satz. \r\n<P><B>description.first.2sentences</B><BR>Die ersten beiden S&auml;tze.<P><B>description.first.3sentences</B><BR>Die \r\nersten 3 S&auml;tze.<P><B>description.first.4sentences</B><BR>Die ersten 4 S&auml;tze.<br><br>\r\n\r\n<B>attachment.box</B><br>\r\nGibt eine Standard WebGUI Anlagen-Box aus mit Icon, Dateiname und Anlageindikator. \r\n<br><br>\r\n<B>attachment.icon</B><br>\r\nDie URL zum Icon-Symbol f&uuml;r diesen Anlagentyp.\r\n<br><br>\r\n<B>attachment.name</B><br>\r\nDer Dateiname f&uuml;r diese Anlage.\r\n<br><br>\r\n<B>attachment.url</B><br>\r\nDie URL zum Downloaden dieser Anlage. \r\n<br><br>\r\n<B>image.thumbnail</B><br>\r\nDie URL zum Thumbnail f&uuml;r die angef&uuml;gte Grafik.\r\n<br><br>\r\n<B>image.url</B><br>\r\nDie URL zur angef&uuml;gten Grafik. \r\n<br><br>\r\n<B>post.label</B><br>\r\nDas &uuml;bersetzte Label um Kommentare zu diesem Artikel hinzuzuf&uuml;gen. \r\n<br><br>\r\n<B>post.URL</B><br>\r\nDie URL um einen Kommentar zu diesem Artikel hinzuzuf&uuml;gen. \r\n<br><br>\r\n<B>replies.count</B><br>\r\nDie Anzahl d!\r\n er Kommentare zu diesem Artikel. \r\n<br><br>\r\n<B>replies.label</B><br>\r\nDer &uuml;bersetzte Text, der anzeigt, dass Sie die Kommentare anschauen k&ouml;nnen..\r\n<br><br> \r\n\r\n<B>replies.url</B><br>\r\nDie URL, um die Kommentare zu diesem Artikel anzuschauen.<P><B>firstPage</B><BR>Ein \r\nLink zur ersten Seite des Paginators.<P><B>lastPage</B><BR>Ein Link zur letzten \r\nSeite des Paginators.<P><B>nextPage</B><BR>Ein Link zur n&auml;ch!
 sten Seite des Paginators<P><B>previousPage</B><BR>Ein \r\nLink zur vorherigen Seite des Paginators.<P><B>pageList</B><BR>Ein Link zu einer \r\nListe aller Seiten des Artikels.<P><B>multiplePages</B><BR>Eine Bedingungsvariable, \r\nfalls der Artikel mehr als eine Seite enth&auml;lt.<P><B>isFirstPage</B><BR>Eine \r\nBedingungsvariable. ob es sich um die erste Seite handelt.<P><B>isLastPage</B><BR>Eine \r\nBedingungsvariable, ob es sich um die letzte Seite handelt.</P>', 1055240868);
