insert into international (internationalId,languageId,namespace,message,lastUpdated,context) values (79,1,'MessageBoard','A message board can contain one or more forums. The following is the list of properties attached to each forum.\r\n\r\n<p>\r\n\r\n<b>Title</b><br>\r\nThe title of the forum.\r\n<p>\r\n\r\n<b>Description</b><br>\r\nThe description of the forum.\r\n<p>\r\n\r\n<b>NOTE:</b> All of the properties of the forum system are also here. See that help page for details.', 1066584480,NULL);
