insert into international (internationalId,languageId,namespace,message,lastUpdated) values (43,2,'Product','Merkmale beschreiben die technischen Details Ihres Produks<br><br>\r\n<b>Bezeichnung</b><br>\r\nDer Typ des Merkmals. Zum Beispiel H&ouml;he, Breite, Gewicht, Farbe. Sie k&ouml;nnen Merkmale, die Sie bereits f&uuml;r andere Produkte definiert haben, &uuml;bernehmen.<br><br>\r\n<b>Merkmal</b><br>\r\nDer aktuelle Wert des Merkmals. Wenn Sie z. B. als Bezeichnung \"Farbe\" gew&auml;hlt haben, k&ouml;nnen Sie hier als Wert \"blau\", \"rot\" usw. definieren.<br><br>\r\n<b>Einheiten</b><br>\r\nDie Masseinheiten f&uuml;r dieses Merkmal. Wenn Sie als Beispiel \"H&ouml;he\" f&uuml;r die Bezeichnung gew&auml;hlt haben, k&ouml;nnen Sie hier z. B. \"Zentimeter\" angeben.<br><br>\r\n<b>Weitere Merkmale hinzuf&uuml;gen?</b><br>\r\nHier k&ouml;nnen Sie weitere Merkmale definieren.', 1058172127);
