insert into international (internationalId,languageId,namespace,message,lastUpdated) values (11,2,'HttpProxy','Das  HTTP Proxy Wobject ist ein sehr m&auml;chtiges Tool. Damit k&ouml;nnen Sie externe \r\nSeiten und deren Anwendungen in ihre Seite integrieren. Wenn Sie z. B. ein Webmailsystem \r\nhaben, dass Sie in ihrem Intranet verf&uuml;gbar machen m&ouml;chten, dann k&ouml;nnen Sie \r\ndas HTTP-Proxy Wobject daf&uuml;r benutzen.<P><B>URL</B><BR>Die Start URL f&uuml;r den \r\nProxy (nicht zu verwechseln mit einem Proxyserver). \r\n<P><B>Weiterleitungen(Redirects) folgen?</B><BR>Manchmal ist die URL zu einer \r\nSeite eine Weiterleitung zu einer anderen Seite. M&ouml;chten Sie dieser Weiterleitung \r\nfolgen?<P><B>Timeout</B><BR>Die Zeit, die WebGUI auf eine Verbindung zu einer \r\nexternen Seite warten soll, bevor der Versuch abgebrochen werden soll. Dies \r\nist sinnvoll, wenn die externe Seite nicht erreichbar ist. \r\n<P><B>Style \r\nentfernen?</B><BR>M&ouml;chte Sie dasStylesheet der externen Seiten entfernen, damit \r\nihr eigener Style &uuml;bernommen wird?<P><B>Inhalt filtern</B><BR>W&auml;hlen Sie die \r\nStufe der Filterung des Inhalts (z. B. Javascript entfernen).<P><B>Proxy-Speicherung anderer Domains erlauben?</B><BR>Wenn Sie eine Seite, wie z. \r\nB. Yahoo in den HTTP-Proxy nehmen: m&ouml;chten Sie dann auch \r\ndie Links, zu denen die andere Seite (in dem Fall Yahoo) verweist, ebenfalls \r\n&uuml;bernehmen, oder nicht?</P>', 1055245941);
