insert into international (internationalId,languageId,namespace,message,lastUpdated) values (939,2,'WebGUI','<p>Wenn Sie ein Thema, dass Sie erstellt haben, l&ouml;schen, so entfernen Sie nur \r\ndas Thema selbst - die Elemente, die dieses Thema enth&auml;lt, werden nicht gel&ouml;scht.</p>\r\n<p>Andererseites aber, wenn Sie ein importiertes Thema l&ouml;schen, so werden auch \r\ndie in diesem Thema enthaltenen Elemente (Grafiken etc.) gel&ouml;scht! Achten Sie \r\ndaher darauf, dass Sie Elemente, die in dem importierten Thema enthalten waren, \r\nauch wirklich nicht mehr ben&ouml;tigen.</p>\r\n', 1055245028);
