insert into international (internationalId,languageId,namespace,message,lastUpdated) values (626,2,'WebGUI','Wobjects (fr&uuml;her auch als  Widgets bezeichnet) sind die wirkliche St&auml;rke von \r\nWebGUI. Wobjects sind kleine einbindbare Applikationen, die f&uuml;r WebGUI entwickelt \r\nwurden. Diskussionsforen und Abstimmungen sind Beispiele f&uuml;r Wobjects. \r\n<P>Um einer Seite ein Wobject hinzuzuf&uuml;gen, m&uuml;ssen sie zuerst die Seite aufrufen, \r\nin die das Wobject soll. Dann w&auml;hlen Sie ein Wobject ihrer Wahl aus dem oberen \r\nlinken Bereich (&quot;Inhalt hinzuf&uuml;gen ... &quot;) aus. Zu jedem Woject gibt \r\nes eine eigene Hilfe, daher schauen sie sich diese vorher an, wenn sie nicht \r\nwissen, welchen Zweck bzw. welche Funktionen das Wobject hat.<P><I>Style Sheets</I>: Alle Wobjects besitzen \r\neine Style-Sheet Klasse und ID. \r\n<P>Die Style-Sheet Klasse besteht aus dem Word  &quot;wobject&quot; plus dem \r\nTyp des Wobjects. Daher w&uuml;rde f&uuml;r eine Abstimmung die Style-Sheet Klasse &quot;wobjectPoll&quot; \r\nlauten. Die Klasse geh&ouml;rt dann zu allen Wobjects dieses Typs im System. \r\n<P>Die Style-Sheet ID besteht aus dem Wort &quot;wobjectId&quot; plus der Wobject Id f&uuml;r \r\ndiese Wobject Instanz. F&uuml;r einen Artikel mit der Wobject Id  94 w&uuml;rde die ID \r\ndann  &quot;wobjectId94&quot; lauten.</P>', 1048589531);
delete from international where languageId=2 and namespace='WebGUI' and internationalId=623;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (623,2,'WebGUI','<A href=\"http://www.w3.org/Style/CSS/\">Cascading Style Sheets (CSS)</A> sind \r\neine gute M&ouml;glichkeit, um das &quot;Look and Feel&quot;, also das Aussehen einer \r\nSeite zu bestimmen. Sie werden sehr weitreichend in WebGUI benutzt.<P>Sollten \r\nSie mit der Benutzung von CSS nicht vertraut sein: <A href=\"http://www.plainblack.com/\">Plain Black Software</A> bietet \r\nSchulungsbereiche in XHTML und CSS an. Alternativ k&ouml;nnen Sie sich den exellenten \r\nCSS Editor von Bradsoft, <A href=\"http://www.bradsoft.com/topstyle/index.asp\">Top Style</A> \r\nanschauen.<P>Nachfolgend eine Liste von Style Klassen, die das Aussehen von \r\nWebGUI bestimmen: WebGUI: \r\n<P><B>A</B><BR>Die Links.<P><B>BODY</B><BR>Das Standard-Setup aller Seiten innerhalb \r\neines Styles. \r\n<P><B>H1</B><BR>Die &Uuml;berschriften einer jeden Seite. \r\n<P><B>.accountOptions</B><BR>DieLink, die unter den Login und Zugangsdaten \r\nFormularen angezeigt werden. \r\n<P><B>.adminBar </B><BR>Die Leiste, die im Kopf der Seite erscheint, wenn Sie \r\nim Editiermodus sind (Administrationsmen&uuml;)<P><B>.content</B><BR>Der Haupt Inhaltsbereich \r\nauf allen Seiten dieses Styles \r\n<P><B>.formDescription </B><BR>Die Zeichen der Formelemente (z. B. im Mailformular \r\nder Text &quot;Von: &quot;)<P><B>.formSubtext </B><BR>Die Zeichen unterhalb \r\nvon Form-Elementen<P><B>.highlight </B><BR>Bedeutet ein hervorgehobenes Element, \r\n so zum Beispiel k&ouml;nnen Sie hiermit den Beitrag, der gerade gelesen wird, hervorheben.<P><B>.horizontalMenu </B><BR>Das \r\nhorizontale Menu (sofern Sie eins verwenden)<P><B>.pagination </B><BR>Die &quot;Vorher&quot; \r\n/ &quot;Weiter&quot; Links auf Seiten, die sich &uuml;ber mehrere Seiten erstrecken.<P><B>.selectedMenuItem</B><BR>Hiermit \r\nk&ouml;nnen Sie in einigen der Menu-Makros die aktuelle Seite hervorheben.<P><B>.tableData </B><BR>Die \r\nDatenreihen u.a.im Diskussionsforum und im Benutzerbeitragssystem.<P><B>.tableHeader</B><BR>Die \r\nSpaltenk&ouml;pfe u.a .im Diskussionsforum und im Benutzerbeitragssystem.<P><B>.tableMenu </B><BR>Das \r\nMenu &nbsp;u.a .im Diskussionsforum und im Benutzerbeitragssystem.<P><B>.verticalMenu </B><BR>Das \r\nvertikale Men&uuml; (sofern Sie eines verwenden)<P><I><B>Beachte:</B></I> Einige \r\nWobjects und Makros haben noch ihre eigenen zus&auml;tzlichen Stylesheet Klassen, \r\ndie in den jeweiligen Hilfebereichen n&auml;her erl&auml;utert sind.</P>', 1048589344);
