insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,2,'SiteMap','Nachfolgend die Liste der Template Variablen f&uuml;r die Sitemap Templates. \r\n<P><b>page_loop</b><BR>Diese Schleife umfasst all Ihre Seiten der Sitemap. \r\n<BLOCKQUOTE><b>page.indent</b><BR>Abstand, wie weit diese Seite im Verzeichnisbaum \r\n    einger&uuml;ckt werden soll. \r\n<P><b>page.url</b><BR>Die URL zur Seite. \r\n<P><b>page.id</b><BR>Die ID f&uuml;r diese Seite, die WebGUI intern benutzt. \r\n<P><b>page.title</b><BR>Der Seitentitel. \r\n<P><b>page.menutitle</b><BR>Der Titel, wie die Seite in der Navigation erscheinen \r\n    soll. \r\n<P><b>page.synopsis</b><BR>Die Beschreibung des Inhalts (Synopsis)&nbsp;f&uuml;r \r\n    diese Seite (sofern vorhanden).. \r\n<P><b>page.isRoot</b><BR>Eine Variable, ob diese Seite root-Seite ist, oder \r\n    nicht.</P>\r\n    <p><b>page.isTop</b><BR>Eine Bedingung, ob diese Seite auf der oberen Ebene \r\n    der Navigationsstruktur ist oder nicht</p>\r\n</blockquote>', 1048243530);
