insert into international (internationalId,languageId,namespace,message,lastUpdated) values (615,2,'WebGUI','Gruppen werden zum Aufteilen von Rechten und Pflichten innherhalb des WebGUI \r\nSystems ben&ouml;tigt. Wenn sie z. b. eine Seite &auml;hnlich einer Klassenraum Situation \r\nerstellen m&uuml;ssen. In diesem Fall w&uuml;rden Sie f&uuml;r jede einzelne Klasse eine eigene \r\nGruppe erstellen, die sie dann den entsprechenden Seiten zuordnen k&ouml;nnten.<P>Es \r\ngibt bereits einige voreingestellte Gruppen in WebGUI:<P><B>Admins</B><BR>Admins \r\nsind User mit unbegrenzten Rechten in WebGUI. Ein Benutzer sollte dieser Gruppe \r\nnur hinzugef&uuml;gt werden, wenn der das System &uuml;berblicken kann. Gew&ouml;hnlich werden \r\n1 bis 3 Personen dieser Gruppe zugeordnet.<P><B>Content Managers</B><BR>Content Manager \r\nsind Benutzer, die die Berechtigung zum Hinzuf&uuml;gen, Bearbeiten und l&ouml;schen von \r\nInhalten f&uuml;r die verschiedenen Bereich der Site haben. Diese Gruppe sollte nicht \r\ndazu benutzt werden, individuelle Content-Bereiche der Seite zu kontrollieren, \r\nsondern um festzulegen, ob &nbsp;ein Benutzer Inhalt bearbeiten kann oder nicht. \r\nSie sollten zus&auml;tzliche Gruppen f&uuml;r die separaten Bereiche der Site erstellen. \r\n<P><B>Everyone (Jeder)</B><BR>Everyone ist eine \'magische\' Gruppe, der niemand \r\nhinzugef&uuml;gt werden kann, aber alle Mitglieder der Site sind Teil davon. M&ouml;chten \r\nSie ihre Seite f&uuml;r Besucher und registrierte Benutzer gleichermassen &ouml;ffnen, \r\ndann benutzen Sie diese Gruppe daf&uuml;r.<P><B>Package Managers (Paket Manager)</B><BR>Benutzer \r\nmit der Berechtigung, Pakete zu erstellen, zu bearbeiten, zu l&ouml;schen und anzuwenden.<P><B>Registrierte \r\nBenutzer</B><BR>Sobald ein User dem System hinzugef&uuml;gt wird, befindet er sich \r\nin dieser Gruppe. Er sollte aus dieser Gruppe nur entfernt werden, wenn sein \r\nAccount gel&ouml;scht ist oder wenn sie einen St&ouml;renfried sperren wollen.<P><B>Style Managers</B><BR>Benutzer \r\nmit der Berechtigung, Styles zu bearbeiten. Dieses Recht enth&auml;lt aber nicht \r\ndas Recht, Berechtigungen f&uuml;r eine Seite zu vergeben.<P><B>Template Managers</B><BR>Benutzer \r\nmit der Berechtigung, Templates f&uuml;r diese Seite zu bearbeiten.<P><B>Visitors \r\n(Besucher)</B><BR>Besucher sind diejenigen Benutzer, die nicht eingeloggt sind. \r\n</P>', 1044964822);
