insert into international (internationalId,languageId,namespace,message,lastUpdated) values (637,2,'WebGUI','<B>Vorname</B><BR>Der Vorname des Benutzers. \r\n<P><B>Zweiter Vorname</B><BR>Ein weiterer Vorname des Benutzers. \r\n<P><B>Nachname</B><BR>Der Nachname des Benutzers. \r\n<P><B>Email Adresse</B><BR>Die Emailadresse des Benutzers. Diese muss nur angegeben \r\nwerden, wenn der Benutzer Funktionen nutzen m&ouml;chte, die eine Emailadresse ben&ouml;tigen. \r\n<P><B>ICQ UIN</B><BR>Die <A href=\"http://www.icq.com/\">ICQ</A> UIN ist die &quot;User \r\nID Number&quot; im ICQ Network. ICQ ist eine sehr bekannte Messaging Plattform. \r\n\r\n<P><B>AIM Id</B><BR>Die Account Id f&uuml;r den <A href=\"http://www.aim.com/\">AOL \r\nInstant Messenger</A> .<P><B>MSN Messenger Id</B><BR>Die Account Id f&uuml;r das \r\n <A \r\nhref=\"http://messenger.msn.com/\">Microsoft Network Instant Messenger system. \r\n\r\n</a><P><B>Yahoo! Messenger Id</B><BR>Die Account Id f&uuml;r den <A \r\nhref=\"http://messenger.yahoo.com/\">Yahoo! Instant Messenger</A> system. \r\n<P><B>Mobiltelefon</B><BR>Die Mobilfunknummer des Benutzers. \r\n<P><B>Pager</B><BR>Die Pager Telefonnummer. \r\n<P><B>Email zu Pager Gateway</B><BR>Die Pager Email Adresse. \r\n<P><B>Private Angaben</B><BR>Die privaten Adressangaben. \r\n<P><B>Gesch&auml;ftliche Angaben</B><BR>Die gesch&auml;ftlichen Adressangaben. \r\n<P><B>Geschlecht</B><BR>Das Geschlecht des Benutzers. \r\n<P><B>Geburtsdatum</B><BR>Das Geburtsdatum des Benutzers.\r\n<p> <B>Sprache</B><BR>In welcher Sprache sollen systembedingte Meldungen angezeigt \r\nwerden?. \r\n</p>\r\n<P><B>Zeitabweichung</B><BR>Die Anzahl der Stunden  (plus oder minus), die die \r\nZeit des Benutzers von der Serverzeit abweicht. Dies wird benutzt, um die einzelnen \r\nZeitzonen anzugleichen. \r\n<P><B>Erster Tag der Woche</B><BR>Der erste Wochentag im Kalender. Zum Beispiel \r\nist in den USA der erste Wochentag der Sonntag und in Deutschland ist es der \r\nMontag. \r\n<P><B>Datumsformat</B><BR>In welchem Format soll das Datum erscheinen? \r\n<P><B>Zeitformat</B><BR>In welchem Format soll die Zeit angezeigt werden? \r\n<P><B>Diskussions Layout</B><BR>Sollen Diskussionen in einer flachen oder beitragsbasierten \r\nStruktur angezeigt werden? In einer flachen Struktur werden die Beitr&auml;ge in \r\nder Reihenfolge, wie sie angelegt wurden, angezeigt. In der beitragsbasierten \r\nStruktur wird eine hierarchische Liste der Antworten, so wie sie erstellt wurden, \r\nangezeigt.<P><B>Benachrichtigungen</B><BR>Wie soll der Benutzer &uuml;ber den Eingang \r\nneuer Nachrichtigen informiert werden? </P>', 1044961519);
