insert into international (internationalId,languageId,namespace,message,lastUpdated) values (620,2,'WebGUI','Wie diese Funktion bereits andeutet werden Sie eine Gruppe l&ouml;schen und alle \r\ndarin befindlichen Benutzer aus dieser Gruppe zu entfernen. Achten Sie darauf, \r\ndass Benutzer in dieser Gruppe nicht vielleicht die Berechtigung f&uuml;r Seiten \r\nverlieren etc. \r\n<P>Wie mit jedem L&ouml;schvorgang werden Sie gefragt, ob sie sich wirklich sicher \r\nsind, diese Gruppe zu l&ouml;schen. Wenn Sie mit &quot;Ja&quot; antworten, wird \r\nder L&ouml;schvorgang durchgef&uuml;hrt, andernfalls gelangen sie zur vorherigen Seite \r\nzur&uuml;ck.</P>', 1044377232);
