insert into international (internationalId,languageId,namespace,message,lastUpdated) values (613,2,'WebGUI','Benutzer sind die Konten, die Berechtigungen zu verschiedenen Bereichen in WebGUI \r\nhaben. Es gibt 2 Standard-Benutzer: Admin und Visitor. \r\n<P></P>\r\n<P><I><B>Admin</B><BR>Unter Admin versteht man genau das, was man unter diesem \r\nBegriff erwartet. Dies ist ein Benutzer mit unbegrenzten Berechtigungen in der \r\nWebGUI Umgebung. </I></P>\r\n<P><I><B>Visitor</B><BR>Der Visitor(Besucher) ist das genaue Gegenteil des Admins. \r\nDer Visitor hat keine Rechte und ein Besucher, der nicht eingeloggt ist, wird \r\nvom System als Visitor erkannt</I></P>\r\n<P><I><B>Neuen Benutzer hinzuf&uuml;gen.</B><BR>Klicken Sie hier, um zur &quot;Benutzer \r\nhinzuf&uuml;gen&quot; Seite zu kommen. \r\n</I></P>\r\n<P><I><B>Suchen</B><BR>Sie k&ouml;nnen nach Benutzernamen und Emailadressen suchen. \r\nDie Suche kann hier auch eingeschr&auml;nkt werden, z. B. auf die Suche nach aktivierten \r\nBenutzern.</I></P>', 1044375306);
