insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,2,'FileManager','Dies ist eine Liste von Template Variablen, die Sie in Downloadmanager Templates \r\nbenutzen k&ouml;nnen. \r\n<P><b>titleColumn.url</b><BR>Die URL, um nach dem Titel zu sortieren. \r\n<P><b>titleColumn.label</b><BR>Die &uuml;bersetzte Bezeichnung des Titels. \r\n<P><b>descriptionColumn.url</b><BR>Die URL, um nach der Beschreibung zu sortieren. \r\n<p><b>descriptionColumn.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die Beschreibung. \r\n</p>\r\n<P><b>dateColumn.url</b><BR>Die  URL, um nach dem Upload-Datum zu sortieren. \r\n<p><b>dateColumn.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r das Upload-Datum. \r\n</p>\r\n<P><b>search.form</b><BR>WebGUI\'s Power Suchformular. \r\n<P><b>search.url</b><BR>Die URL, um den Suchmodus an- oder abzuschalten. \r\n<P><b>search.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r den Suchlink. \r\n<P><b>addfile.url</b><BR>Die URL, um eine Datei hinzuz!\n uf&uuml;gen. \r\n<P><b>addfile.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diesen Link. \r\n<P><b>file_loop</b><BR>Eine Schleife, die Informationen &uuml;ber jede upgeloadete \r\nDatei enth&auml;lt. \r\n<BLOCKQUOTE><b>file.canView</b><BR>Eine Bedingungsvariable, ob der Benutzer \r\n    die Berechtigung hat, diese Datei anzuschauen. \r\n<P><b>file.controls</b><BR>Die WebGUI Verwaltungscontrols f&uuml;r diese Datei. \r\n<P><b>file.title</b><BR>Der Dateititel. \r\n<P><b>file.version1.name</b><BR>Der Dateiname der ersten Version dieser Datei. \r\n\r\n<P><b>file.version1.url</b><BR>Die Download-URL f&uuml;r diese erste Version der \r\n    Datei. \r\n<P><b>file.version1.icon</b><BR>Die URL zum Dateityp-Icon f&uuml;r die erste Version \r\n    der Datei. \r\n<P><b>file.version1.size</b><BR>Die Dateigr&ouml;sse der ersten Version der Datei. \r\n<P><b>file.version1.type</b><BR>Der Dateityp (siehe auch file.version1.icon). \r\n<P><b>file.version1.thumbnail</b><BR>Die URL zum Vorschaubild (Thumbnail)f&uuml;r \r\n    die ers!\n te Version dieser Datei. \r\n<P><b>file.version1.isImage</b><BR>Eine V\nariable um zu ermitteln, ob diese Datei \r\n    eine Grafik ist oder nicht. \r\n<P><b>file.version2.name</b><BR>Der Dateiname f&uuml;r die Alternative #1 dieser \r\n    Datei. \r\n<P><b>file.version2.url</b><BR>Die Download-URL f&uuml;r Alternative #1. \r\n<P><b>file.version2.icon</b><BR>Die URL zum Dateityp-Icon f&uuml;r Alternative #1. \r\n<P><b>file.version2.size</b><BR>Die Dateigr&ouml;sse f&uuml;r Alternative #1. \r\n<P><b>file.version2.type</b><BR>Der Dateityp von Alternative #1. \r\n<P><b>file.version2.thumbnail</b><BR>Die URL zum Vorschaubild (Thumbnail) f&uuml;r \r\n    Alternative #1. \r\n<P><b>file.version2.isImage</b><BR>Eine Variable um zu ermitteln, ob es sich \r\n    bei Alternative #1 um eine Grafik handelt oder nicht. \r\n<P><b>file.version3.name</b><BR>Der Dateiname f&uuml;r die Alternative #2 dieser \r\n    Datei. \r\n<P><b>file.version3.url</b><BR>Die Download-URL f&uuml;r Alternative #2. \r\n<P><b>file.version3.icon</b><BR>Die URL zum Dateityp-Icon f&uuml;r Alternative #2. \r\n<P><b>file.version3.s!\n ize</b><BR>Die Dateigr&ouml;sse f&uuml;r Alternative #2. \r\n<P><b>file.version3.type</b><BR>Der Dateityp von Alternative #2. \r\n<P><b>file.version3.thumbnail</b><BR>Die URL zum Vorschaubild (Thumbnail) f&uuml;r \r\n    Alternative #2. \r\n<P><b>file.version3.isImage</b><BR>Eine Variable um zu ermitteln, ob es sich \r\n    bei Alternative #2 um eine Grafik handelt oder nicht. \r\n<P><b>file.description</b><BR>Die Beschreibung f&uuml;r die Datei (f&uuml;r alle 3 Versionen \r\n    gleich). \r\n<P><b>file.date</b><BR>Das Datum, an dem die Datei(en) hochgeladen wurde(n). \r\n<P><b>file.time</b><BR>Die Uhrzeit, an dem die Datei(en) hochgeladen wurde(n). \r\n<P></P></BLOCKQUOTE>\r\n<P><b>noresults.message</b><BR>Eine &uuml;bersetzte Nachricht, dass es keine Dateien \r\nzum Downloaden f&uuml;r diesen Benutzer gibt. \r\n<P><b>noresults</b><BR>Eine Variable, um zu ermitteln, ob es Dateien f&uuml;r diesen\r\nBenutzer zum Downloaden gibt. \r\n<P><b>firstPage</b><BR> Ein Link zur ersten Seite. \r\n<P><b>lastPage</b><BR>Ein!\n  Link zur letzten Seite. \r\n<P><b>nextPage</b><BR>Ein Link zur n&auml;chst\nen Seite. \r\n<P><b>previousPage</b><BR>Ein Link zur vorherigen Seite. \r\n<P><b>pageList</b><BR>Eine Liste aller Seiten. \r\n<P><b>multiplePages</b><BR>Eine Variable um zu ermitteln, ob es mehrere Seiten \r\ngibt.</P>', 1041633620);
