insert into international (internationalId,languageId,namespace,message,lastUpdated) values (633,2,'WebGUI','Vereinfacht ausgedr&uuml;ckt, sind Startseiten (auch root genannt) Seiten mit keiner dar&uuml;berliegenden Seite. \r\nDie erste und wichtigeste Startseite in WebGUI ist die &quot;Home&quot; Seite. \r\nViele Anwender f&uuml;gen niemals weitere Startseiten ein, aber einige Poweruser schon. \r\nDiese User erstellen neue Startseiten aus verschiedenen Gr&uuml;nden, vielleicht erstellen sie Sammelpunkte f&uuml;r Contentmanager oder vielleicht einen versteckten Bereich f&uuml;r Admin-Tools. Oder wom&ouml;glich einfach eine neue Startseite, um die Suchmaschine zu platzieren. \r\n<p>M&ouml;glichkeiten zum Einsatz mehrerer Startseiten bieten sich viele... lassen Sie Ihren Ideen freien Lauf.</p>', 1041631109);
