insert into international (internationalId,languageId,namespace,message,lastUpdated) values (63,2,'Product','Nachfolgend eine Liste der Template Variabeln, die in Produkt Templates verf&uuml;gbar \r\nsind. \r\n<P><b>brochure.icon</b><BR>Die URL zum Icon des Prospekts. \r\n<P><b>brochure.url</b><BR>Die URL um dieses Prospekt zu downloaden. \r\n<P><b>brochure.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese URL. \r\n<P><b>warranty.icon</b><BR>Die URL zum Icon der Garantie. \r\n<P><b>warranty.url</b><BR>Die URL um die Garantie zu downloaden. \r\n<P><b>warranty.label</b><BR>Die Bezeichnung f&uuml;r diese URL. \r\n<P><b>image1</b><BR>Die URL zur Produktgrafik 1. \r\n<P><b>thumbnail1</b><BR>Die URL zum Vorschaubild der Produktgrafik 1. \r\n<P><b>image2</b><BR>Die URL zur Produktgrafik 2. \r\n<P><b>thumbnail2</b><BR>Die URL zum Vorschaubild der Produktgrafik 2. \r\n<P><b>image3</b><BR>Die URL zur Produktgrafik 3. \r\n<P><b>thumbnail3</b><BR>Die URL zum Vorschaubild der Produktgrafik 3. \r\n!\n <P><b>addfeature.url</b><BR>Die URL um eine Produkteigenschaft (Feature) hinzuzuf&uuml;gen. \r\n<P><b>addfeature.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese URL. \r\n<P><b>feature_loop</b><BR>Diese Schleife enth&auml;lt alle Produkteigenschaften, \r\ndie zu diesem Produkt geh&ouml;ren. \r\n<BLOCKQUOTE><b>feature.controls</b><BR> WebGUI Verwaltungselemente f&uuml;r diese \r\n    Eigenschaft. \r\n<P><b>feature.feature</b><BR>Der Text der Produkteigenschaft. \r\n<P></P></BLOCKQUOTE>\r\n<P><b>addbenefit.url</b><BR>Die URL um Vorteile hinzuzuf&uuml;gen. \r\n<P><b>addbenefit.label</b><BR>Die &uuml;bersetzte Bezeichnung &nbsp;f&uuml;r diese URL. \r\n<P><b>benefit_loop</b><BR>Diese Schleife enth&auml;lt alle Vorteile dieses Produktes. \r\n<BLOCKQUOTE><b>benefit.benefit</b><BR>Der Vorteil selbst (Text des Vorteils). \r\n<P><b>benefit.controls</b><BR>WebGUI Verwaltungselemente f&uuml;r diesen Vorteil. \r\n<P></P></BLOCKQUOTE>\r\n<P><b>addspecification.url</b><BR>Die URL um Merkmale hinzuzuf&uuml;gen. \r\n<P><b>addspecification!\n .label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese URL. \r\n<P><b>spec\nification_loop</b><BR>Eine Liste von Merkmalen zu diesem Produkt. \r\n<BLOCKQUOTE><b>specification.controls</b><BR>WebGUI Verwaltungselemente f&uuml;r \r\n    dieses Merkmal. \r\n<P><b>specification.specification</b><BR>Der Text des Merkmals selbst. \r\n<P><b>specification.units</b><BR>Die Einheiten f&uuml;r dieses Merkmal, z.B. Zentimeter \r\n<P><b>specification.label</b><BR>Die Bezeichnung f&uuml;r dieses Merkmal, z. B. H&ouml;he \r\n<P></P></BLOCKQUOTE>\r\n<P><b>addaccessory.url</b><BR>Die URL zum Hinzuf&uuml;gen von Zubeh&ouml;r. \r\n<P><b>addaccessory.label</b><BR>Die &uuml;bersetzte Bezeichnung &nbsp;f&uuml;r diese URL. \r\n\r\n<P><b>accessory_loop</b><BR>Diese Schleife enth&auml;lt all das&nbsp;zu diesem Produkt \r\nverbundene Zubeh&ouml;r. \r\n<BLOCKQUOTE><b>accessory.url</b><BR>Die URL zum verbundenen Zubeh&ouml;r. \r\n<P><b>accessory.title</b><BR>Der Titel des verbundenen Zubeh&ouml;rs. \r\n<P><b>accessory.controls</b><BR>WebGUI Verwaltungselemente f&uuml;r dieses Zubeh&ouml;r. \r\n<P></P></BLOCKQUOTE>\r\n<P><b>addRelatedProduct.url<!\n /b><BR>Die URL um ein verwandtes Produkt hinzuzuf&uuml;gen. \r\n<P><b>addRelatedProduct.label</b><BR>Die &uuml;bersetzte Bezeichnung &nbsp;f&uuml;r diese \r\nURL. \r\n<P><b>relatedproduct.url</b><BR>Die URL zu diesem verwandten Produkt. \r\n<P><b>relatedproduct.title</b><BR>Der Titel des verwandten Produkts. \r\n<P><b>relatedproduct.controls</b><BR>WebGUI Verwaltungselemente f&uuml;r dieses verwandte \r\nProdukt.</P>', 1041627712);
