insert into international (internationalId,languageId,namespace,message,lastUpdated) values (608,2,'WebGUI','Eine Seite zu l&ouml;schen k&ouml;nnte ein grosses Durcheinander verursachen, wenn Sie \r\nsich nicht sicher sind, was Sie tun. Wenn Sie eine Seite l&ouml;schen, l&ouml;schen Sie \r\nauch den kompletten Inhalt dieser Seite, sowie alle Unterseiten, die zu dieser \r\nSeite verbunden sind an auch deren Inhalt. Versichern Sie sich, dass Sie allen \r\nInhalt, den Sie noch ben&ouml;tigen, verschoben haben, bevor Sie zu L&ouml;schen beginnen. \r\n<P>Wie es in der Regel bei L&ouml;schvorg&auml;ngen so &uuml;blich ist, werden Sie vorher gefragt, \r\nob Sie sich sicher sind, dass Sie diese Seite l&ouml;schen m&ouml;chten. Wenn Sie nun \r\nmit &quot;Ja&quot; antworten, wird die Seite end&uuml;ltig gel&ouml;scht. Wenn Sie mit \"nein\" antworten gelangen Sie wieder zur vorhergehenden Seite</P>', 1041615928);
