insert into international (internationalId,languageId,namespace,message,lastUpdated) values (41,2,'Product','Eigenschaften(Features) sind Verkaufkriterien f&uuml;r ein Produkt, vielleicht auch Gr&uuml;nde daf&uuml;r, genau dieses Produkt zu kaufen.\r\n<br><br>\r\n<b>Eigenschaft</b><br>\r\nSie k&ouml;nnen eine neue Eigenschaft angeben oder die Eigenschaft eines anderen Produktes &uuml;bernehmen.\r\n<br><br>\r\n<b>Eine weitere Eigenschaft eingeben?</b><br>\r\nM&ouml;chten Sie weitere Eigenschaften definieren?', 1041612440);
