insert into international (internationalId,languageId,namespace,message,lastUpdated) values (74,2,'MessageBoard','Nachfolgend eine Liste von Template Variablen f&uuml;r Diskussionsforen-Templates. \r\n<P><b>canPost</b><BR>Eine Bedingungs-Variable, ob der Benutzer die Berechtigung \r\nhat, einen neuen Beitrag zu schreiben. \r\n<P><b>post.url</b><BR>Die  URL zum Schreiben neuer Beitr&auml;ge. \r\n<P><b>post.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r diese  URL. \r\n<P><b>search.url</b><BR>Die URL zum Durchsuchen des Forums. \r\n<P><b>search.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die Such-URL. \r\n<P><b>subject.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die Betreffspalte. \r\n<P><b>user.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die Benutzerspalte. \r\n<P><b>date.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die Datums-Spalte. \r\n<P><b>views.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die Ansichten-Spalte. \r\n<P><b>replies.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die Antworten!\n -Spalte. \r\n<P><b>last.label</b><BR>Die &uuml;bersetzte Bezeichnung f&uuml;r die &quot;letzte Antwort&quot;-Spalte. \r\n<P><b>message_loop</b><BR>eine Schleife die jede Nachricht enth&auml;lt. \r\n<BLOCKQUOTE><b>last.url</b><BR>Die URL zur letzten Antwort dieses Beitrages. \r\n<P><b>last.subject</b><BR>Der Betreff der letzten Antwort zu diesem Beitrag. \r\n<P><b>last.username</b><BR>Der Benutzername, der diese Antwort verfasst hat. \r\n<P><b>last.userProfile</b><BR>Die URL zum Profil dieses Benutzers. \r\n<P><b>last.date</b><BR>Das Datum, an dem die letzte Antwort zu diesem Beitrag \r\n    geschrieben wurde. \r\n<P><b>message.subject</b><BR>Der Betreff des Beitrages. \r\n<P><b>message.date</b><BR>Das Datum, an dem dieser Beitrag geschrieben wurde. \r\n<P><b>message.user</b><BR>Der Benutzer, der diesen Beitrag verfasst hat. \r\n<P><b>message.views</b><BR>Wie oft wurde dieser Beitrag angeschaut. \r\n<P><b>message.replies</b><BR>Die Anzahl der Antworten zu diesem Thread/Thema/Beitrag. \r\n<!\n P><b>message.url</b><BR>Die URL, um diesen Beitrag anzuschauen. \r\n<P\n><b>message.currentUser</b><BR>Eine Bedingungsvariable, ob der aktuelle Benutzer \r\n    auch der Benutzer ist, der diesen Beitrag ver&ouml;ffentlicht hat. \r\n<P><b>message.status</b><BR>Der Status dieses Beitrages (ausstehend, freigegeben, \r\nabgelehnt). \r\n<P></P></BLOCKQUOTE>\r\n<P><b>firstPage</b><BR>Ein Link zur ersten Seite. \r\n<P><b>lastPage</b><BR>Ein Link zur letzten Seite. \r\n<P><b>nextPage</b><BR>Ein Link zur n&auml;chsten Seite. \r\n<P><b>previousPage</b><BR>Ein Link zur vorherigen Seite. \r\n<P><b>pageList</b><BR>Eine Liste der Links zu allen Seiten. \r\n<P><b>multiplePages</b><BR>Eine Bedingungsvariable, ob es mehr als eine Seite \r\ngibt.</P>', 1041610808);
