insert into international (internationalId,languageId,namespace,message,lastUpdated) values (958,12,'WebGUI','<P>Utklippstavlen er en spesiell systemlokasjon som midlertidig inneholder \r\nelementer som er klippet ut eller kopiert. Enheter fra utklippstavlen kaan limes \r\ninn p nye lokasjoner.</P>\r\n<P>Utklippstavlens innhold kan administreres individuelt. Du kan slette eller \r\nlime inn enheter ved  velge riktig ikon. Du kan ogs flytte hele innholdet p \r\nutklippstavlen til sppla ved  velge "Tm utklippstavle". \r\n<P><B>Tittel<BR></B>Navnet p enheten p utklippstavlen. Du kan vise enheten ved \r\n velge tittelen.</P>\r\n<P><B>Type</B><BR>Typen innhold. F.eks. En pakke, en artikkel, hendelseskalender \r\nosv. \r\n<P><B>Utklippsdato&nbsp;Date</B><BR>Dato og tid nr enheten ble plassert p \r\nutklippstavlen. \r\n<P><STRONG>Forrige lokasjon<BR></STRONG>Lokasjonen hvor enheten sist var \r\nplassert. Du kan vise lokasjonen ved  velge lokasjonen.</P>\r\n<P><STRONG>Brukernavn</STRONG><BR>Brukernavnet til den brukeren som plasserte \r\nelementet p utklippstavlen. Dette feltet er bare synelig i en delt \r\nutklippstavle hvor en administrator administrerer innholdet p \r\nutklippstavlen.</P>', 1066008701);
