insert into international (internationalId,languageId,namespace,message,lastUpdated) values (934,12,'WebGUI','<P>Opprettelse og redigering av tema er en noks enkel prosess. Frst setter du \r\nopp noen grunnleggende egenskaper for temaet, og s legger du elementer til \r\nTema.</P>\r\n<P>De grunnleggende egenskapene for et tema kan ikke endres p med unntak av p \r\ndet nettstedet det ble opprettet p. N flger definisjon p de grunnleggende \r\negenskapene for et Tema:</P>\r\n<P><B>Theme ID</B><BR>den unike ID for dette tema p dette WebGUI nettstedet. \r\nDenne ID vil endres om Tema er importert til et nytt nettsted.</P>\r\n<P><B>Theme Name</B><BR>Dette er det unike navnet til et tema. Det m vre \r\nunikt p hvert eneste nettsted det vil bli importert til. Navnet vil ikke endres \r\np andre nettsteder.</P>\r\n<P><B>Theme Designer</B><BR>Navnet til den person eller firma som skapte dette \r\ntema.</P>\r\n<P><B>Designer URL</B><BR>URL\'n til nettstedet til skaperen av temaet. Om du \r\nnsker  reklamere for nettstedet som lager Tema for WebGUI, s er dette plassen \r\nfor  skape oppmerksomhet.</P>', 1066008507);
delete from international where languageId=12 and namespace='WebGUI' and internationalId=932;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (932,12,'WebGUI','Tema er en rask mte  installere nye stiler, Templates og Collateral til en WebGUI site. De er ogs effektive for  flytte de samme fra en site til en annen.\r\n\r\nTIP: Nr du bygger et tema, srg for  navngi elementene (stiler, templates, Collateral) med navn som er unike i forhold til Temaet. Dette er fornuftig slik at brukerene kan finne tilhrende elementer og for  unng navnekonflikter med andre elementer.', 1066008443);
delete from international where languageId=12 and namespace='WebGUI' and internationalId=926;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (926,12,'WebGUI','Dette Tema ble opprettet med en nyere versjon av WebGUI enn den som er installert p ditt system. Du m oppgradere fr du kan installere dette tema.', 1066008380);
delete from international where languageId=12 and namespace='WebGUI' and internationalId=925;
