insert into international (internationalId,languageId,namespace,message,lastUpdated) values (828,12,'WebGUI','<P>De fleste Wobject har templates som tillater deg  endre utseende p \r\nbrukergrensesnittet. De wobjectene som har templates har alle et felles sett med \r\nvariabler som kan brukes i layouten. I tillegg til deres egne lokale variable. \r\nDe flgende er en liste over felles variable som deles mellom alle Wobjects.</P>\r\n<P><STRONG>title</STRONG><BR>The title for this wobject. </P>\r\n<P><STRONG>displayTitle</STRONG><BR>A conditional variable for whether or not \r\nthe title should be displayed. </P>\r\n<P><STRONG>description<BR></STRONG>The description of this wobject. </P>\r\n<P><STRONG>wobjectId<BR></STRONG>The unique identifier that WebGUI uses to \r\ncontrol this wobject. <BR></P>\r\n<P><B>isShortcut</B><BR>A conditional indicating if this wobject is a shortcut \r\nto an original wobject. \r\n<P><B>originalURL</B><BR>If this wobject is a shortcut, then this URL will \r\ndirect you to the original wobject. </P>', 1066008344);
delete from international where languageId=12 and namespace='WebGUI' and internationalId=638;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (638,12,'WebGUI','Templates brukes for  pvirke hvordan innhold vises frem i WebGUI. Det flger med mange Templates i WebGUI. Bruker du Template hndteringssystemet kan du legge til dine egne Templates for  sikre at systemet fr et utseende nyaktig som du nsker det.', 1066008301);
delete from international where languageId=12 and namespace='WebGUI' and internationalId=629;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (629,12,'WebGUI','<STRONG>Bruk Delt UtklippsTavle?<BR></STRONG>Enabler en enkelt systemomfattende \r\nutklippstavle delt av alle brukerene. Standard er utklippstavle for hver enkelt \r\nbruker.<BR>\r\n<P><STRONG>Bruke Delt Sppel?<BR></STRONG>Enabler en enkelt systemdekkende \r\nsppel delt av alle brukere. Standard er sppel for hver enkelt \r\nbruker.<BR><B><BR>If proxied, use real client IP address?</B><BR>If enabled and \r\nif the environment variable HTTP_X_FORWARDED_FOR is present, it\'s value will be \r\nused in place of REMOTE_ADDRESS as the client browser\'s IP address. This is \r\nrequired for IP based groups to function properly in reverse-proxied, \r\nload-balanced system architectures. In these environments, all requests would \r\notherwise appear to come from the same host, namely the proxy server. If you are \r\nuncertain if you need this setting enabled, you should probably leave it turned \r\noff. </P>\r\n<P><B>Forhindre Proxy Caching</B><BR>Noen selskaper har proxy servere som skaper \r\nproblemer for WebGUI. Om du opplever problemer med WebGUI, og du har en proxy \r\nserver, prv  sette denne til "Ja". Vr klar over at WebGUI sine URL\'r ikke \r\nblir like brukervennlige etterp.<BR><B><BR>Vis debugging?</B><BR>Vis debugging \r\ninformasjon i resultatene fra WebGUI. Dette er frst og fremst nyttig for WebGUI \r\nutviklere, men kan ogs brukes av administratorer som prver  lse et \r\nproblem.</P>\r\n<P><STRONG>Flg Side statistikker?<BR></STRONG>WebGUI kan ta vare p en del \r\nstatistikker p bruken av nettstedet ditt. Men dette vil pvirke ytelsen p \r\nserveren og databasen vil vokse raskere. Bruk denne bare om du ikke har et \r\nekstern Web statistikk program.<BR></P>', 1066008244);
delete from international where languageId=12 and namespace='WebGUI' and internationalId=614;
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (614,12,'WebGUI','<P>Stiler brukes for  hndtere utseende p dine WebGUI sider. Med WebGUI kan du \r\nopprette et ubegrenset antall Stiler, slik at ditt nettsted kan ha s mange \r\nutseender som du nsker. Du kan ha noen sider som ligner din firmabrosjyre og \r\nnoen sider som ligner Yahoo. Du kan tilogmed ha noen sider som ser ut som sider \r\ni en bok. Ved hjelp av "Hndtere Stiler" vil du ha ubegrenset kontroll over \r\ndesignet p nettstedet.</P>\r\n<P>Det finnes flere Stiler innebygget i WebGUI. De frste av disse benyttes av \r\nWebGUI og br ikke slettes eller endres.</P>\r\n<P><B>Clipboard</B><BR>Denne Stilen benyttes av Utklippstavlen.<BR><B><BR>Fail \r\nSafe</B><BR>Nr du sletter en Stil som fremdeles benyttes p en side eller \r\nflere, vil Fail Safe Stilen benyttes p disse sidene. Denne siden har hvit \r\nbakgrunn og enkel navigasjon.<BR><B><BR>Make Page Printable</B><BR>Denne Stilen \r\nbrukes nr ^r; makroen brukes p en side og brukeren klikker p den. Dene Stilen \r\nlar deg legge til en enkel logo og opphavsrett beskjed p dine utskriftbare \r\nsider.</P>\r\n<P>&nbsp;</P>\r\n<P><B>Packages</B><BR>Denne stilen benyttes av pakke hndterings \r\nsystemet.<BR><B><BR>Trash</B><BR>Denne stilen benyttes av Sppel \r\nsystemet.<BR></P>', 1066008139);
