insert into international (internationalId,languageId,namespace,message,lastUpdated) values (74,12,'Poll','<P>De flgende variablene er tilgjengelige for Avstemnings \r\n(Poll)&nbsp;Template:</P>\r\n<P>&nbsp;<B>canVote</B><BR>A condition indicating whether the user has the right \r\nto vote on this poll. </P>\r\n<P><B>question</B><BR>The poll question. \r\n<P><B>form.start</B><BR>The beginning of the vote form. \r\n<P><B>answer_loop</B><BR>A loop containing information about the answers in the \r\npoll. \r\n<P>\r\n<BLOCKQUOTE><B>answer.form</B><BR>The radio button for this answer. \r\n<P><B>answer.text</B><BR>The text of the answer. \r\n<P><B>answer.number</B><BR>The number of this answer. As in 1, 2, 3, etc. \r\n<P><B>answer.graphWidth</B><BR>The width that the graph should be rendered for \r\nthis answer. Based upon a percentage of the total graph size. \r\n<P><B>answer.percent</B><BR>The percentage of the vote that this answer has \r\nreceived. \r\n<P><B>answer.total</B><BR>The total number of votes that this answer has \r\nreceived. \r\n<P></P></BLOCKQUOTE><B>form.submit</B><BR>The submit button for the poll form. \r\n<P><B>form.end</B><BR>The end of the poll form. \r\n<P><B>responses.label</B><BR>The label for the total responses. "Total Votes" \r\n<P><B>responses.total</B><BR>The total number of votes that have been placed on \r\nthis poll. </P>', 1066008032);
