insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,12,'LinkList','<P>Her flger en liste over template variabler som er tilgjengelige i Link List \r\nTemplates.</P>\r\n<P><STRONG>addlink.url</STRONG><BR>URL\'n for  legge til en link i listen.</P>\r\n<P><STRONG>addlink.label</STRONG><BR>Den oversatte etiketten for addlink.url</P>\r\n<P><STRONG>canEdit<BR></STRONG>En indikator som forteller om en bruker kan \r\nredigere linkene.</P>\r\n<P><STRONG>link_loop</STRONG><BR>Denne loop\'en inneholder all informasjon om \r\nhver enkelt link.</P>\r\n<BLOCKQUOTE dir=ltr style="MARGIN-RIGHT: 0px">\r\n<P><STRONG>link.url</STRONG><BR>URL\'n for denne linken</P>\r\n<P><STRONG>link.name<BR></STRONG>Teksten som linkes.</P>\r\n<P><STRONG>link.controls</STRONG><BR>WebGUI\'s management kontroller for denne \r\nlinken.</P>\r\n<P><STRONG>link.newwindow</STRONG><BR>bryter for valg av om linken skal pnes i \r\nnytt vindu eller ikke.</P>\r\n<P><STRONG>link.description</STRONG><BR>Beskrivelsen av linken.</P></BLOCKQUOTE>', 1066007892);
