insert into international (internationalId,languageId,namespace,message,lastUpdated) values (11,12,'HttpProxy','<P>HTTP Proxy wobject er et kraftig verkty. Det lar deg pakke inn og vise et \r\neksternt web sted eller applikasjon p din egen side. For eksempel, om du har et \r\nWebMail system som du nsker dine anstatte skal kunne bruke igjennom \r\nintranettet, da kan du bruke HTTP Proxy for  oppn dette.</P>\r\n<P><STRONG>URL</STRONG><BR>Start URL\'n for proxyen.</P>\r\n<P><STRONG>Flge redirects?</STRONG><BR>Noen ganger er URL\'n til en side bare en \r\nlink til en ny side (Redirect). Vil du flge slike linker nr de dukker opp?</P>\r\n<P><STRONG>Tidsavbrudd</STRONG><BR>Tiden i sekunder som WebGUI skal vente p en \r\nforbindelse fr den gir opp forsket p  n den eksterne siden.</P>\r\n<P><STRONG>Fjerne Style?<BR></STRONG>Vil du fjerne style sheet fra den eksterne \r\nsiden til fordel for din egen style p din egen side?</P>\r\n<P><STRONG>Filtrer innhold?</STRONG><BR>Velg nivet av HTML filtrering som du \r\nnsker  bruke p proxy innholdet.</P>\r\n<P><STRONG>Tillat proxying av andre domener?</STRONG><BR>Om du proxyer en side \r\nsom Yahoo! som linker til andre domener, nsker du da  flge disse linkene til \r\nandre domener, eller skal proxyen stoppe dem nr de forsker  forlate den \r\nopprinnelige siden du har spesifisert?</P>', 1066007833);
