insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,12,'ExtraColumn','<P>Ekstra kolonner lar deg endre utseende p KUN en side. Om du nsker  benytte \r\nflere kolonner p alle sider, br du kanskje vurdere  endre "Stil" som hrer \r\ntil sidene dine eller velge en Template i steden for en ekstra kolonne. WebGUI \r\nlegger opp til at denne forsvinner som valg etterhvert.</P>\r\n<P>Kolonner er alltid lagt til fra venstre mot hyre. Eksisterende innhold p \r\nsiden vil derfor havne p venstre side av den nye kolonnen.</P>\r\n<P><STRONG>Spacer<BR></STRONG>Spacer er den ekstra avstanden mellom ditt \r\neksisterende innhold og den nye kolonnen. Den mles i pixels.</P>\r\n<P><STRONG>Bredde<BR></STRONG>Bredde er den faktiske bredden av kolonnen som \r\nlegges til. Den mles i pixels.</P>\r\n<P><STRONG>StilArk Class</STRONG><BR>Som standard vil "content" stil (som er den \r\nstil som benyttes p alt innhold "body" som siten din benytter) bli benyttet til \r\nalle kolonner. Men om du har laget en egen stil spesielt for kolonner, s benytt \r\ngjerne den.</P>', 1066007747);
